import React, { useState, useEffect } from 'react';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import PostCard from '../components/PostCard';
import CreatePost from '../components/CreatePost';
import { Loader2, RefreshCw } from 'lucide-react';

const Home = () => {
  const { posts, loading, error, getPosts } = usePost();
  const { user } = useAuth();
  const [page, setPage] = useState(1);
  const [refreshing, setRefreshing] = useState(false);

  useEffect(() => {
    getPosts(page);
  }, [page]);

  const handleRefresh = async () => {
    setRefreshing(true);
    await getPosts(1);
    setPage(1);
    setRefreshing(false);
  };

  const handleLoadMore = () => {
    setPage(prev => prev + 1);
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="mb-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">Home</h1>
          <button
            onClick={handleRefresh}
            disabled={refreshing}
            className="flex items-center space-x-2 px-3 py-1.5 text-sm text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-md transition-colors disabled:opacity-50"
          >
            <RefreshCw className={`h-4 w-4 ${refreshing ? 'animate-spin' : ''}`} />
            <span>Refresh</span>
          </button>
        </div>

        {/* Create Post Section */}
        <CreatePost />
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-md p-4 mb-6">
          <div className="flex items-center">
            <span className="text-sm text-red-600">{error}</span>
          </div>
        </div>
      )}

      {/* Loading State */}
      {loading && posts.length === 0 && (
        <div className="flex items-center justify-center py-12">
          <div className="text-center">
            <Loader2 className="animate-spin h-8 w-8 text-primary-600 mx-auto mb-4" />
            <p className="text-gray-600">Loading posts...</p>
          </div>
        </div>
      )}

      {/* Posts Feed */}
      {!loading && posts.length === 0 && !error && (
        <div className="text-center py-12">
          <div className="bg-gray-100 rounded-full w-16 h-16 mx-auto mb-4 flex items-center justify-center">
            <svg className="h-8 w-8 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 11V9a2 2 0 012-2m0 0V5a2 2 0 012-2h6a2 2 0 012 2v2M7 7h10" />
            </svg>
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No posts yet</h3>
          <p className="text-gray-600 mb-4">
            {user?.following?.length > 0 
              ? "Posts from people you follow will appear here"
              : "Start following people to see their posts here"
            }
          </p>
          <p className="text-sm text-gray-500">
            Or create your first post above to get started!
          </p>
        </div>
      )}

      {/* Posts List */}
      {posts.length > 0 && (
        <div className="space-y-6">
          {posts.map((post) => (
            <PostCard key={post._id} post={post} />
          ))}
        </div>
      )}

      {/* Load More Button */}
      {posts.length > 0 && !loading && (
        <div className="text-center mt-8">
          <button
            onClick={handleLoadMore}
            className="px-6 py-2 text-sm font-medium text-primary-600 hover:text-primary-700 hover:bg-primary-50 rounded-md transition-colors"
          >
            Load more posts
          </button>
        </div>
      )}

      {/* Loading More State */}
      {loading && posts.length > 0 && (
        <div className="flex items-center justify-center py-8">
          <div className="text-center">
            <Loader2 className="animate-spin h-6 w-6 text-primary-600 mx-auto mb-2" />
            <p className="text-sm text-gray-600">Loading more posts...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
