import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { ArrowLeft, Loader2, Edit, Trash2 } from 'lucide-react';
import CommentSection from '../components/CommentSection';

const PostDetail = () => {
  const { postId } = useParams();
  const { currentPost, getPost, loading, error } = usePost();
  const { user } = useAuth();
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    getPost(postId);
  }, [postId]);

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        await deletePost(postId);
        // Redirect to home after deletion
        window.location.href = '/';
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      const diffHours = Math.ceil(diffTime / (1000 * 60 * 60));
      if (diffHours === 0) {
        const diffMinutes = Math.ceil(diffTime / (1000 * 60));
        return diffMinutes <= 1 ? 'Just now' : `${diffMinutes}m ago`;
      }
      return `${diffHours}h ago`;
    } else if (diffDays === 1) {
      return 'Yesterday';
    } else if (diffDays < 7) {
      return `${diffDays}d ago`;
    } else {
      return date.toLocaleDateString('en-US', { 
        month: 'short', 
        day: 'numeric',
        year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
      });
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <Loader2 className="animate-spin h-8 w-8 text-primary-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading post...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-2xl mx-auto">
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <div className="text-red-600">{error}</div>
        </div>
      </div>
    );
  }

  if (!currentPost) {
    return (
      <div className="max-w-2xl mx-auto">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8 text-center">
          <h3 className="text-lg font-medium text-gray-900 mb-2">Post not found</h3>
          <p className="text-gray-600 mb-4">The post you're looking for doesn't exist.</p>
          <Link
            to="/"
            className="inline-flex items-center px-4 py-2 bg-primary-600 text-white text-sm font-medium rounded-md hover:bg-primary-700 transition-colors"
          >
            <ArrowLeft size={16} className="mr-2" />
            Back to Home
          </Link>
        </div>
      </div>
    );
  }

  const isAuthor = user?._id === currentPost.author?._id;
  const likesCount = currentPost.likesCount || currentPost.likes?.length || 0;
  const commentsCount = currentPost.commentsCount || currentPost.comments?.length || 0;

  return (
    <div className="max-w-2xl mx-auto">
      {/* Back Button */}
      <div className="mb-4">
        <Link
          to="/"
          className="inline-flex items-center text-gray-600 hover:text-gray-900 transition-colors"
        >
          <ArrowLeft size={20} className="mr-2" />
          Back to Home
        </Link>
      </div>

      {/* Post */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
        {/* Post Header */}
        <div className="p-6">
          <div className="flex items-start justify-between">
            <div className="flex items-center space-x-3">
              <Link to={`/users/${currentPost.author?._id}`}>
                <img
                  src={currentPost.author?.profileImage || 'https://via.placeholder.com/40/3b82f6/ffffff?text=U'}
                  alt={currentPost.author?.username}
                  className="w-10 h-10 rounded-full object-cover hover:opacity-90 transition-opacity"
                />
              </Link>
              <div>
                <Link 
                  to={`/users/${currentPost.author?._id}`}
                  className="font-medium text-gray-900 hover:text-primary-600 transition-colors"
                >
                  {currentPost.author?.username}
                </Link>
                <p className="text-sm text-gray-500">{formatDate(currentPost.createdAt)}</p>
              </div>
            </div>

            {/* Dropdown Menu */}
            {isAuthor && (
              <div className="relative">
                <button
                  onClick={() => setShowDropdown(!showDropdown)}
                  className="p-1 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-full transition-colors"
                >
                  <svg className="h-5 w-5" fill="currentColor" viewBox="0 0 20 20">
                    <path d="M10 6a2 2 0 110-4 2 2 0 010 4zM10 12a2 2 0 110-4 2 2 0 010 4zM10 18a2 2 0 110-4 2 2 0 010 4z" />
                  </svg>
                </button>

                {showDropdown && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg border border-gray-200 z-10">
                    <div className="py-1">
                      <Link
                        to={`/posts/${currentPost._id}/edit`}
                        className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 transition-colors"
                        onClick={() => setShowDropdown(false)}
                      >
                        <Edit size={16} className="mr-2" />
                        Edit Post
                      </Link>
                      <button
                        onClick={() => {
                          handleDelete();
                          setShowDropdown(false);
                        }}
                        className="flex items-center w-full px-4 py-2 text-sm text-red-600 hover:bg-red-50 transition-colors"
                      >
                        <Trash2 size={16} className="mr-2" />
                        Delete Post
                      </button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        {/* Post Content */}
        <div className="px-6 pb-6">
          <div className="text-gray-900 whitespace-pre-wrap break-words text-lg">
            {currentPost.content}
          </div>
        </div>

        {/* Post Image */}
        {currentPost.image && (
          <div className="px-6 pb-6">
            <img
              src={currentPost.image}
              alt="Post content"
              className="w-full rounded-lg object-cover max-h-96 hover:opacity-95 transition-opacity"
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
          </div>
        )}

        {/* Post Stats */}
        <div className="px-6 py-4 border-t border-gray-100">
          <div className="flex items-center space-x-6 text-sm text-gray-500">
            <span>{likesCount} like{likesCount !== 1 ? 's' : ''}</span>
            <span>{commentsCount} comment{commentsCount !== 1 ? 's' : ''}</span>
          </div>
        </div>
      </div>

      {/* Comments Section */}
      <div className="mt-6">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="p-4 border-b border-gray-100">
            <h3 className="text-lg font-medium text-gray-900">
              Comments ({commentsCount})
            </h3>
          </div>
          <CommentSection postId={postId} />
        </div>
      </div>
    </div>
  );
};

export default PostDetail;
