import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { ArrowLeft, Save, X, Image, Sparkles ,Badge } from 'lucide-react';
import { Card, Form, Button, Alert, Spinner } from 'react-bootstrap';

const EditPost = () => {
  const { postId } = useParams();
  const { currentPost, getPost, updatePost, loading, error } = usePost();
  const { user } = useAuth();
  const navigate = useNavigate();

  const [content, setContent] = useState('');
  const [image, setImage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showImageInput, setShowImageInput] = useState(false);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    if (postId) {
      getPost(postId);
    }
  }, [postId]);

  useEffect(() => {
    if (currentPost) {
      setContent(currentPost.content || '');
      setImage(currentPost.image || '');
    }
  }, [currentPost]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!content.trim()) {
      return;
    }

    setIsSubmitting(true);
    setSuccess(false);

    try {
      const result = await updatePost(postId, {
        content: content.trim(),
        image: image.trim() || null
      });

      if (result.success) {
        setSuccess(true);
        setTimeout(() => {
          navigate(`/posts/${postId}`);
        }, 1500);
      }
    } catch (error) {
      console.error('Error updating post:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleImageToggle = () => {
    setShowImageInput(!showImageInput);
  };

  const handleImageRemove = () => {
    setImage('');
    setShowImageInput(false);
  };

  const charCount = content.length;
  const maxChars = 2000;
  const isNearLimit = charCount > maxChars * 0.9;
  const isAtLimit = charCount >= maxChars;

  if (loading) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Card className="text-center p-5 border-0 glass">
          <Card.Body>
            <div className="text-center">
              <div className="w-16 h-16 mx-auto mb-4 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl d-flex align-items-center justify-content-center">
                <Spinner className="text-purple-600" />
              </div>
              <h3 className="h5 text-dark mb-2 fw-bold">Loading Post</h3>
              <p className="text-muted">Please wait while we fetch the post...</p>
            </div>
          </Card.Body>
        </Card>
      </div>
    );
  }

  if (!currentPost) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Alert variant="danger" className="fade-in">
          Post not found or you don't have permission to edit it.
        </Alert>
      </div>
    );
  }

  if (currentPost.author?._id !== user?._id) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Alert variant="warning" className="fade-in">
          You don't have permission to edit this post.
        </Alert>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <div className="container-fluid px-4 py-4">
        {/* Header */}
        <Card className="glass rounded-3 shadow-xl mb-4 border-0 fade-in">
          <Card.Body className="p-3">
            <div className="d-flex align-items-center">
              <Button
                as={Link}
                to={`/posts/${postId}`}
                className="btn-hover px-3 py-2 rounded-xl me-3"
                variant="outline-primary"
              >
                <ArrowLeft className="w-4 h-4 me-2" />
                Back
              </Button>
              <div className="flex-grow-1">
                <h2 className="h4 fw-bold text-dark mb-0">Edit Post</h2>
                <p className="text-muted small mb-0">Update your post content and image</p>
              </div>
              <div className="d-flex align-items-center gap-2">
                <div className="w-8 h-8 bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl d-flex align-items-center justify-content-center">
                  <Sparkles className="w-4 h-4 text-white" />
                </div>
              </div>
            </div>
          </Card.Body>
        </Card>

        {/* Edit Form */}
        <Card className="glass rounded-3 shadow-xl p-4 fade-in border-0">
          <Card.Body>
            <Form onSubmit={handleSubmit}>
              <div className="d-flex gap-3 mb-3">
                <div className="position-relative">
                  <img
                    src={user?.profileImage || 'https://via.placeholder.com/48/667eea/ffffff?text=U'}
                    alt={user?.username}
                    className="w-12 h-12 rounded-full object-cover border-2 border-white/50"
                  />
                  <div className="position-absolute bottom-0 end-0 w-4 h-4 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full border-2 border-white"></div>
                </div>

                <div className="flex-grow-1">
                  <div className="d-flex align-items-center gap-2 mb-2">
                    <h6 className="fw-bold text-dark mb-0">{user?.username}</h6>
                    <Badge bg="light" text="dark" className="px-2 py-1 rounded-pill small">
                      Editing
                    </Badge>
                  </div>

                  <Form.Control
                    as="textarea"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    placeholder="Share your thoughts with the world..."
                    className="border-0 bg-transparent text-lg resize-none form-input"
                    rows={4}
                    maxLength={maxChars}
                    disabled={isSubmitting}
                  />

                  {/* Image Preview */}
                  {image && (
                    <div className="mt-3 position-relative">
                      <img
                        src={image}
                        alt="Post image"
                        className="w-100 rounded-xl object-cover shadow-lg transition-all duration-300 hover:scale-[1.02]"
                        style={{ maxWidth: '400px' }}
                        onError={(e) => {
                          e.target.style.display = 'none';
                          setImage('');
                        }}
                      />
                      <Button
                        variant="danger"
                        size="sm"
                        onClick={handleImageRemove}
                        className="position-absolute top-2 end-2 bg-danger/80 text-white rounded-circle p-1 hover:bg-danger transition-all duration-200"
                      >
                        <X size={16} />
                      </Button>
                    </div>
                  )}

                  {/* Image Input */}
                  {showImageInput && !image && (
                    <div className="mt-3 slide-up">
                      <Form.Control
                        type="url"
                        value={image}
                        onChange={(e) => setImage(e.target.value)}
                        placeholder="Enter image URL (optional)"
                        className="bg-white/80 backdrop-blur-sm form-input"
                        disabled={isSubmitting}
                      />
                    </div>
                  )}
                </div>
              </div>

              {/* Actions Bar */}
              <div className="d-flex justify-content-between align-items-center mt-3 pt-3 border-top border-white/10">
                <div className="d-flex align-items-center gap-2">
                  <Button
                    variant="link"
                    onClick={handleImageToggle}
                    disabled={isSubmitting}
                    className={`d-flex align-items-center text-decoration-none ${
                      showImageInput || image
                        ? 'text-purple-600' 
                        : 'text-muted hover:text-purple-600'
                    }`}
                  >
                    <div className={`p-1.5 rounded-lg transition-colors ${
                      showImageInput || image ? 'bg-purple-100' : 'hover:bg-purple-100'
                    }`}>
                      <Image size={18} />
                    </div>
                    <span className="small">{image ? 'Image Added' : 'Add Image'}</span>
                  </Button>
                </div>

                <div className="d-flex align-items-center gap-3">
                  {/* Character Count */}
                  <div className="d-flex align-items-center">
                    <span className={`small fw-medium ${
                      isAtLimit ? 'text-danger' : isNearLimit ? 'text-warning' : 'text-muted'
                    }`}>
                      {charCount}/{maxChars}
                    </span>
                    {isNearLimit && (
                      <div className={`w-2 h-2 rounded-circle animate-pulse ${
                        isAtLimit ? 'bg-danger' : 'bg-warning'
                      }`}></div>
                    )}
                  </div>

                  {/* Submit Button */}
                  <Button
                    type="submit"
                    disabled={!content.trim() || isSubmitting || isAtLimit}
                    className="btn-hover glow px-4 py-2"
                    variant="primary"
                  >
                    {isSubmitting ? (
                      <>
                        <Spinner className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></Spinner>
                        Updating...
                      </>
                    ) : (
                      <>
                        <Save size={16} className="me-2" />
                        Update Post
                      </>
                    )}
                  </Button>
                </div>
              </div>

              {/* Success Message */}
              {success && (
                <Alert variant="success" className="mt-3 fade-in">
                  <div className="d-flex align-items-center">
                    <span>Post updated successfully! Redirecting...</span>
                  </div>
                </Alert>
              )}

              {/* Error Message */}
              {error && (
                <Alert variant="danger" className="mt-3 fade-in">
                  <div className="d-flex justify-content-between align-items-center">
                    <span>{error}</span>
                  </div>
                </Alert>
              )}
            </Form>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default EditPost;
