import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { Heart, MessageCircle, Share2, MoreHorizontal, Edit, Trash2, X, Sparkles } from 'lucide-react';
import { Card, Button, Dropdown } from 'react-bootstrap';
import CommentSection from './CommentSection';

const PostCard = ({ post }) => {
  const { user } = useAuth();
  const { deletePost, toggleLike } = usePost();
  const [showComments, setShowComments] = useState(false);
  const [isLiking, setIsLiking] = useState(false);

  const isAuthor = user?._id === post.author?._id;
  const isLiked = post.likes?.includes(user?._id);
  const likesCount = post.likesCount || post.likes?.length || 0;
  const commentsCount = post.commentsCount || post.comments?.length || 0;

  const handleLike = async () => {
    if (isLiking) return;

    setIsLiking(true);
    try {
      await toggleLike(post._id);
    } catch (error) {
      console.error('Error toggling like:', error);
    } finally {
      setIsLiking(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        await deletePost(post._id);
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      const diffHours = Math.ceil(diffTime / (1000 * 60 * 60));
      if (diffHours === 0) {
        const diffMinutes = Math.ceil(diffTime / (1000 * 60));
        return diffMinutes <= 1 ? 'Just now' : `${diffMinutes}m ago`;
      }
      return `${diffHours}h ago`;
    } else if (diffDays === 1) {
      return 'Yesterday';
    } else if (diffDays < 7) {
      return `${diffDays}d ago`;
    } else {
      return date.toLocaleDateString('en-US', { 
        month: 'short', 
        day: 'numeric',
        year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
      });
    }
  };

  return (
    <Card className="glass rounded-2xl shadow-xl overflow-hidden card-hover fade-in border-0 mb-4">
      <Card.Body className="p-4">
        {/* Post Header */}
        <div className="d-flex justify-content-between align-items-start mb-3">
          <div className="d-flex align-items-center">
            <Link to={`/users/${post.author?._id}`}>
              <img
                src={post.author?.profileImage || 'https://via.placeholder.com/48/667eea/ffffff?text=U'}
                alt={post.author?.username}
                className="w-12 h-12 rounded-full object-cover hover:scale-110 transition-transform border-2 border-white/50 me-3"
              />
            </Link>
            <div>
              <Link 
                to={`/users/${post.author?._id}`}
                className="fw-semibold text-decoration-none text-dark hover:text-purple-600 transition-colors"
              >
                {post.author?.username}
              </Link>
              <p className="text-muted small">{formatDate(post.createdAt)}</p>
            </div>
          </div>

          {/* Dropdown Menu */}
          {isAuthor && (
            <Dropdown>
              <Dropdown.Toggle 
                variant="link"
                className="text-muted p-2 hover:text-gray-600 hover:bg-white/10 rounded-circle"
              >
                <MoreHorizontal size={20} />
              </Dropdown.Toggle>

              <Dropdown.Menu align="end" className="glass border-0 shadow-lg">
                <Dropdown.Item 
                  as={Link}
                  to={`/posts/${post._id}/edit`}
                  className="d-flex align-items-center"
                >
                  <Edit size={16} className="me-2" />
                  Edit Post
                </Dropdown.Item>
                <Dropdown.Item 
                  onClick={handleDelete}
                  className="d-flex align-items-center text-danger"
                >
                  <Trash2 size={16} className="me-2" />
                  Delete Post
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          )}
        </div>

        {/* Post Content */}
        <div className="mb-3">
          <p className="text-dark mb-0 text-lg lh-base">
            {post.content}
          </p>
        </div>

        {/* Post Image */}
        {post.image && (
          <div className="mb-3">
            <div className="position-relative">
              <img
                src={post.image}
                alt="Post content"
                className="w-100 rounded-xl object-cover shadow-lg transition-all duration-300 hover:scale-[1.02]"
                style={{ maxHeight: '400px' }}
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
              <div className="position-absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300 rounded-xl"></div>
            </div>
          </div>
        )}

        {/* Post Actions */}
        <div className="border-top border-white/10 pt-3">
          <div className="d-flex justify-content-between align-items-center">
            <div className="d-flex align-items-center">
              {/* Like Button */}
              <Button
                variant="link"
                onClick={handleLike}
                disabled={isLiking}
                className={`d-flex align-items-center text-decoration-none transition-all duration-300 ${
                  isLiked 
                    ? 'text-danger' 
                    : 'text-muted hover:text-danger'
                }`}
              >
                <div className={`p-2 rounded-circle transition-all duration-300 ${
                  isLiked 
                    ? 'bg-danger-subtle text-danger' 
                    : 'hover:bg-danger-subtle'
                }`}>
                  <Heart 
                    size={20} 
                    className={isLiked ? 'fill-current animate-pulse' : ''} 
                  />
                </div>
                <span className="fw-medium ms-1">{likesCount}</span>
              </Button>

              {/* Comment Button */}
              <Button
                variant="link"
                onClick={() => setShowComments(!showComments)}
                className="d-flex align-items-center text-decoration-none text-muted hover:text-purple-600 transition-all duration-300 ms-3"
              >
                <div className="p-2 rounded-circle hover:bg-purple-100 transition-colors">
                  <MessageCircle size={20} />
                </div>
                <span className="fw-medium ms-1">{commentsCount}</span>
              </Button>

              {/* Share Button */}
              <Button
                variant="link"
                className="d-flex align-items-center text-decoration-none text-muted hover:text-purple-600 transition-all duration-300 ms-3"
                onClick={() => {
                  navigator.clipboard.writeText(window.location.origin + `/posts/${post._id}`);
                  // Show toast notification
                  const toast = document.createElement('div');
                  toast.className = 'toast success fixed top-4 right-4 z-50';
                  toast.textContent = 'Link copied to clipboard!';
                  document.body.appendChild(toast);
                  setTimeout(() => toast.remove(), 3000);
                }}
              >
                <div className="p-2 rounded-circle hover:bg-purple-100 transition-colors">
                  <Share2 size={20} />
                </div>
                <span className="fw-medium ms-1">Share</span>
              </Button>
            </div>
          </div>
        </div>
      </Card.Body>

      {/* Comments Section */}
      {showComments && (
        <div className="border-top border-white/10">
          <CommentSection postId={post._id} />
        </div>
      )}
    </Card>
  );
};

export default PostCard;
