import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Send, MessageCircle, Loader2, Heart, Sparkles } from 'lucide-react';
import { Card, Form, Button, Badge, Spinner, Alert } from 'react-bootstrap';

const CommentSection = ({ postId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const { user } = useAuth();

  useEffect(() => {
    fetchComments();
  }, [postId]);

  const fetchComments = async () => {
    try {
      const response = await fetch(`/api/comments/post/${postId}`);
      const data = await response.json();

      if (data.success) {
        setComments(data.data.comments);
      }
    } catch (error) {
      console.error('Error fetching comments:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!newComment.trim()) return;

    setSubmitting(true);

    try {
      const response = await fetch('/api/comments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          postId,
          text: newComment.trim()
        })
      });

      const data = await response.json();

      if (data.success) {
        setComments(prev => [data.data.comment, ...prev]);
        setNewComment('');
      } else {
        console.error('Error creating comment:', data.message);
      }
    } catch (error) {
      console.error('Error creating comment:', error);
    } finally {
      setSubmitting(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffMinutes = Math.ceil(diffTime / (1000 * 60));

    if (diffMinutes <= 1) return 'Just now';
    if (diffMinutes < 60) return `${diffMinutes}m ago`;

    const diffHours = Math.ceil(diffTime / (1000 * 60 * 60));
    if (diffHours < 24) return `${diffHours}h ago`;

    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    if (diffDays < 7) return `${diffDays}d ago`;

    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric',
      year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
    });
  };

  return (
    <div className="p-4">
      {/* Add Comment Form */}
      <Card className="glass rounded-3 shadow-sm mb-4 border-0 fade-in">
        <Card.Body className="p-3">
          <Form onSubmit={handleSubmit}>
            <div className="d-flex align-items-center gap-3">
              <div className="position-relative group">
                <img
                  src={user?.profileImage || 'https://via.placeholder.com/32/3b82f6/ffffff?text=U'}
                  alt={user?.username}
                  className="w-10 h-10 rounded-full object-cover border-2 border-white/50 hover:scale-110 transition-transform"
                />
                <div className="absolute bottom-0 right-0 w-3 h-3 bg-green-500 rounded-full border-2 border-white d-flex align-items-center justify-center group-hover:scale-125 transition-transform">
                  <div className="w-1.5 h-1.5 bg-white rounded-full animate-pulse"></div>
                </div>
              </div>
              <div className="flex-grow-1 d-flex gap-2">
                <Form.Control
                  type="text"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="Write a comment..."
                  className="form-input border-2 rounded-pill shadow-sm focus:shadow-lg transition-all duration-300"
                  disabled={submitting}
                  maxLength={500}
                />
                <Button
                  type="submit"
                  disabled={!newComment.trim() || submitting}
                  className="btn-hover px-3 py-2 rounded-pill d-flex align-items-center justify-content-center"
                  variant="primary"
                >
                  {submitting ? (
                    <Loader2 className="w-4 h-4 animate-spin" />
                  ) : (
                    <Send className="w-4 h-4" />
                  )}
                </Button>
              </div>
            </div>
            <div className="text-end mt-1">
              <small className="text-muted">{newComment.length}/500</small>
            </div>
          </Form>
        </Card.Body>
      </Card>

      {/* Comments List */}
      <div className="space-y-3">
        {loading ? (
          <Card className="text-center p-4 border-0 glass">
            <Card.Body>
              <div className="d-flex align-items-center justify-content-center">
                <div className="w-12 h-12 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl d-flex align-items-center justify-content-center me-3">
                  <Loader2 className="w-6 h-6 text-purple-600 animate-spin" />
                </div>
                <div>
                  <h5 className="h6 text-dark mb-1 fw-bold">Loading Comments</h5>
                  <p className="text-muted small mb-0">Please wait while we fetch comments...</p>
                </div>
              </div>
            </Card.Body>
          </Card>
        ) : comments.length === 0 ? (
          <Card className="text-center p-5 border-0 glass hover:shadow-xl transition-all duration-300">
            <Card.Body>
              <div className="text-center">
                <div className="w-16 h-16 mx-auto mb-3 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl d-flex align-items-center justify-content-center group-hover:scale-110 transition-transform">
                  <MessageCircle className="w-8 h-8 text-gray-400 group-hover:text-gray-600 transition-colors" />
                </div>
                <h5 className="h5 text-dark mb-2 fw-bold">No Comments Yet</h5>
                <p className="text-muted mb-3">Be the first to share your thoughts!</p>
                <div className="d-flex justify-content-center gap-2">
                  <Badge bg="primary" className="px-3 py-2 rounded-pill">
                    <Sparkles className="w-3 h-3 me-1" />
                    Start Conversation
                  </Badge>
                </div>
              </div>
            </Card.Body>
          </Card>
        ) : (
          <div className="space-y-3">
            {comments.map((comment, index) => (
              <Card 
                key={comment._id} 
                className="glass rounded-3 shadow-sm border-0 fade-in hover:shadow-lg transition-all duration-300"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <Card.Body className="p-3">
                  <div className="d-flex gap-3">
                    <div className="position-relative group">
                      <img
                        src={comment.author?.profileImage || 'https://via.placeholder.com/32/3b82f6/ffffff?text=U'}
                        alt={comment.author?.username}
                        className="w-10 h-10 rounded-full object-cover border-2 border-white/50 hover:scale-110 transition-transform cursor-pointer"
                      />
                      <div className="absolute inset-0 w-10 h-10 rounded-full bg-gradient-to-r from-purple-400 to-pink-400 opacity-0 group-hover:opacity-20 transition-opacity duration-300 rounded-full"></div>
                    </div>
                    <div className="flex-grow-1">
                      <div className="d-flex align-items-center justify-content-between mb-2">
                        <div className="d-flex align-items-center gap-2">
                          <h6 className="fw-bold text-dark mb-0 hover:text-purple-600 transition-colors cursor-pointer">
                            {comment.author?.username}
                          </h6>
                          <Badge bg="light" text="dark" className="px-2 py-1 rounded-pill small">
                            {formatDate(comment.createdAt)}
                          </Badge>
                        </div>
                        <Button 
                          variant="link" 
                          className="text-muted p-0 hover:text-red-500 transition-colors"
                        >
                          <Heart className="w-4 h-4" />
                        </Button>
                      </div>
                      <div className="bg-white/50 backdrop-blur-sm rounded-2xl p-3 border border-white/20 hover:bg-white/70 transition-all duration-300">
                        <p className="text-dark mb-0 lh-base">
                          {comment.text}
                        </p>
                      </div>
                    </div>
                  </div>
                </Card.Body>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CommentSection;
