import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { ArrowLeft, Loader2, Edit, Trash2, Heart, MessageCircle, Calendar, MapPin, MoreVertical } from 'lucide-react';
import CommentSection from '../components/CommentSection';
import { Card, Button, Badge, Spinner, Alert, Dropdown } from 'react-bootstrap';

const PostDetail = () => {
  const { postId } = useParams();
  const { currentPost, getPost,deletePost, loading, error } = usePost();
  const { user } = useAuth();
  const [showDropdown, setShowDropdown] = useState(false);

  useEffect(() => {
    getPost(postId);
  }, [postId]);

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        await deletePost(postId);
        // Redirect to home after deletion
        window.location.href = '/';
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      const diffHours = Math.ceil(diffTime / (1000 * 60 * 60));
      if (diffHours === 0) {
        const diffMinutes = Math.ceil(diffTime / (1000 * 60));
        return diffMinutes <= 1 ? 'Just now' : `${diffMinutes}m ago`;
      }
      return `${diffHours}h ago`;
    } else if (diffDays === 1) {
      return 'Yesterday';
    } else if (diffDays < 7) {
      return `${diffDays}d ago`;
    } else {
      return date.toLocaleDateString('en-US', { 
        month: 'short', 
        day: 'numeric',
        year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
      });
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Card className="text-center p-5 border-0 glass">
          <Card.Body>
            <div className="text-center">
              <div className="w-16 h-16 mx-auto mb-4 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl d-flex align-items-center justify-content-center">
                <Loader2 className="w-8 h-8 text-purple-600 animate-spin" />
              </div>
              <h3 className="h5 text-dark mb-2 fw-bold">Loading Post</h3>
              <p className="text-muted">Please wait while we fetch the post details...</p>
            </div>
          </Card.Body>
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Alert variant="danger" className="fade-in">
          <div className="d-flex align-items-center">
            <span className="text-danger">{error}</span>
          </div>
        </Alert>
      </div>
    );
  }

  if (!currentPost) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Card className="text-center p-5 border-0 glass hover:shadow-2xl transition-all duration-300">
          <Card.Body>
            <div className="text-center">
              <div className="w-20 h-20 mx-auto mb-4 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl d-flex align-items-center justify-center group-hover:scale-110 transition-transform">
                <Calendar className="w-10 h-10 text-gray-400 group-hover:text-gray-600 transition-colors" />
              </div>
              <h3 className="h4 text-dark mb-2 fw-bold">Post Not Found</h3>
              <p className="text-muted mb-4">The post you're looking for doesn't exist.</p>
              <Button
                as={Link}
                to="/"
                className="btn-hover px-4 py-2 rounded-xl"
                variant="primary"
              >
                <ArrowLeft className="w-4 h-4 me-2" />
                Back to Home
              </Button>
            </div>
          </Card.Body>
        </Card>
      </div>
    );
  }

  const isAuthor = user?._id === currentPost.author?._id;
  const likesCount = currentPost.likesCount || currentPost.likes?.length || 0;
  const commentsCount = currentPost.commentsCount || currentPost.comments?.length || 0;

  return (
    <div className="min-h-screen">
      <div className="container-fluid px-4 py-4">
        {/* Back Button */}
        <Card className="glass rounded-3 shadow-xl mb-4 border-0 fade-in">
          <Card.Body className="p-3">
            <Button
              as={Link}
              to="/"
              className="btn-hover px-4 py-2 rounded-xl"
              variant="outline-primary"
            >
              <ArrowLeft className="w-4 h-4 me-2" />
              Back to Home
            </Button>
          </Card.Body>
        </Card>

        {/* Post */}
        <Card className="glass rounded-3 shadow-xl overflow-hidden mb-4 border-0 fade-in">
          {/* Post Header */}
          <Card.Body className="p-4">
            <div className="d-flex justify-content-between align-items-start">
              <div className="d-flex align-items-center">
                <Link to={`/users/${currentPost.author?._id}`}>
                  <img
                    src={currentPost.author?.profileImage || 'https://via.placeholder.com/40/3b82f6/ffffff?text=U'}
                    alt={currentPost.author?.username}
                    className="w-12 h-12 rounded-full object-cover border-2 border-white/50 hover:scale-110 transition-transform"
                  />
                </Link>
                <div className="ms-3">
                  <Link 
                    to={`/users/${currentPost.author?._id}`}
                    className="text-decoration-none"
                  >
                    <h5 className="fw-bold text-dark mb-1 hover:text-purple-600 transition-colors">
                      {currentPost.author?.username}
                    </h5>
                  </Link>
                  <div className="d-flex align-items-center">
                    <Calendar className="w-4 h-4 text-muted me-1" />
                    <p className="text-muted small">{formatDate(currentPost.createdAt)}</p>
                  </div>
                </div>
              </div>

              {/* Dropdown Menu */}
              {isAuthor && (
                <Dropdown>
                  <Dropdown.Toggle 
                    variant="link"
                    onClick={() => setShowDropdown(!showDropdown)}
                    className="text-muted p-2 hover:text-gray-600 hover:bg-gray-100 rounded-circle"
                  >
                    <MoreVertical className="w-5 h-5" />
                  </Dropdown.Toggle>

                  <Dropdown.Menu align="end" className="glass border-0 shadow-lg">
                    <Dropdown.Item 
                      as={Link}
                      to={`/posts/${currentPost._id}/edit`}
                      className="d-flex align-items-center"
                    >
                      <Edit size={16} className="me-2" />
                      Edit Post
                    </Dropdown.Item>
                    <Dropdown.Item 
                      onClick={() => {
                        handleDelete();
                        setShowDropdown(false);
                      }}
                      className="d-flex align-items-center text-danger"
                    >
                      <Trash2 size={16} className="me-2" />
                      Delete Post
                    </Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              )}
            </div>
          </Card.Body>
        </Card>

        {/* Post Content */}
        <Card.Body className="px-6 pb-6">
          <div className="text-dark whitespace-pre-wrap break-words text-lg lh-base">
            {currentPost.content}
          </div>
        </Card.Body>

        {/* Post Image */}
        {currentPost.image && (
          <Card.Body className="px-6 pb-6">
            <div className="position-relative">
              <img
                src={currentPost.image}
                alt="Post content"
                className="w-100 rounded-xl object-cover shadow-lg transition-all duration-300 hover:scale-[1.02]"
                style={{ maxHeight: '500px' }}
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
              <div className="position-absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300 rounded-xl"></div>
            </div>
          </Card.Body>
        )}

        {/* Post Stats */}
        <Card>
        <Card.Body className="px-6 py-4 border-top border-white/10">
          <div className="d-flex justify-content-around text-center">
            <div className="text-center p-3 rounded-xl bg-gradient-to-br from-pink-50 to-red-50 hover:from-pink-100 hover:to-red-100 transition-all duration-300 cursor-pointer group">
              <div className="d-flex align-items-center">
                <Heart className="w-5 h-5 me-2" />
                <div className="h5 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{likesCount}</div>
              </div>
              <div className="text-muted small fw-medium">Likes</div>
            </div>
            <div className="text-center p-3 rounded-xl bg-gradient-to-br from-blue-50 to-cyan-50 hover:from-blue-100 hover:to-cyan-100 transition-all duration-300 cursor-pointer group">
              <div className="d-flex align-items-center">
                <MessageCircle className="w-5 h-5 me-2" />
                <div className="h5 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{commentsCount}</div>
              </div>
              <div className="text-muted small fw-medium">Comments</div>
            </div>
          </div>
        </Card.Body>
      </Card>

        {/* Comments Section */}
        <Card className="glass rounded-3 shadow-xl border-0 fade-in">
          <Card.Body className="p-4">
            <div className="d-flex align-items-center mb-4">
              <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-xl d-flex align-items-center justify-center me-3">
                <MessageCircle className="w-4 h-4 text-white" />
              </div>
              <h3 className="h4 fw-bold text-dark mb-0">
                Comments ({commentsCount})
              </h3>
            </div>
            <CommentSection postId={postId} />
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default PostDetail;
