import React, { useState } from 'react';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { Image, Send, X } from 'lucide-react';

const CreatePost = () => {
  const [content, setContent] = useState('');
  const [image, setImage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showImageInput, setShowImageInput] = useState(false);

  const { createPost, error, clearError } = usePost();
  const { user } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!content.trim()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const result = await createPost({
        content: content.trim(),
        image: image.trim() || null
      });

      if (result.success) {
        setContent('');
        setImage('');
        setShowImageInput(false);
      }
    } catch (error) {
      console.error('Error creating post:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleImageToggle = () => {
    setShowImageInput(!showImageInput);
    if (!showImageInput) {
      setImage('');
    }
  };

  const handleImageRemove = () => {
    setImage('');
    setShowImageInput(false);
  };

  const charCount = content.length;
  const maxChars = 2000;
  const isNearLimit = charCount > maxChars * 0.9;
  const isAtLimit = charCount >= maxChars;

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 mb-6">
      <form onSubmit={handleSubmit}>
        <div className="flex space-x-3">
          <img
            src={user?.profileImage || 'https://via.placeholder.com/40/3b82f6/ffffff?text=U'}
            alt={user?.username}
            className="w-10 h-10 rounded-full object-cover flex-shrink-0"
          />

          <div className="flex-1">
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="What's on your mind?"
              className="w-full resize-none border-0 focus:ring-0 text-gray-900 placeholder-gray-500 text-lg"
              rows={3}
              maxLength={maxChars}
              disabled={isSubmitting}
            />

            {/* Image Preview */}
            {image && (
              <div className="mt-3 relative">
                <img
                  src={image}
                  alt="Post image"
                  className="w-full max-w-md rounded-lg object-cover"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    setImage('');
                  }}
                />
                <button
                  type="button"
                  onClick={handleImageRemove}
                  className="absolute top-2 right-2 bg-black bg-opacity-50 text-white rounded-full p-1 hover:bg-opacity-70 transition-all"
                >
                  <X size={16} />
                </button>
              </div>
            )}

            {/* Image Input */}
            {showImageInput && !image && (
              <div className="mt-3">
                <input
                  type="url"
                  value={image}
                  onChange={(e) => setImage(e.target.value)}
                  placeholder="Enter image URL (optional)"
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                  disabled={isSubmitting}
                />
              </div>
            )}
          </div>
        </div>

        {/* Actions Bar */}
        <div className="flex items-center justify-between mt-4 pt-3 border-t border-gray-100">
          <div className="flex items-center space-x-2">
            <button
              type="button"
              onClick={handleImageToggle}
              className={`flex items-center space-x-1 px-3 py-1.5 text-sm rounded-md transition-colors ${
                showImageInput || image
                  ? 'text-primary-600 bg-primary-50'
                  : 'text-gray-600 hover:text-gray-900 hover:bg-gray-100'
              }`}
              disabled={isSubmitting}
            >
              <Image size={18} />
              <span>{image ? 'Image Added' : 'Add Image'}</span>
            </button>
          </div>

          <div className="flex items-center space-x-3">
            {/* Character Count */}
            <span className={`text-xs ${
              isAtLimit ? 'text-red-600' : isNearLimit ? 'text-yellow-600' : 'text-gray-500'
            }`}>
              {charCount}/{maxChars}
            </span>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={!content.trim() || isSubmitting || isAtLimit}
              className="flex items-center space-x-1 px-4 py-1.5 bg-primary-600 text-white text-sm font-medium rounded-md hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            >
              {isSubmitting ? (
                <>
                  <svg className="animate-spin h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  <span>Posting...</span>
                </>
              ) : (
                <>
                  <Send size={16} />
                  <span>Post</span>
                </>
              )}
            </button>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="mt-3 bg-red-50 border border-red-200 rounded-md p-3">
            <div className="flex items-center">
              <span className="text-sm text-red-600">{error}</span>
              <button
                type="button"
                onClick={clearError}
                className="ml-auto text-red-400 hover:text-red-600"
              >
                <X size={16} />
              </button>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default CreatePost;
