import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { Heart, MessageCircle, Share2, MoreHorizontal, Edit, Trash2, X, Sparkles } from 'lucide-react';
import CommentSection from './CommentSection';

const PostCard = ({ post }) => {
  const { user } = useAuth();
  const { deletePost, toggleLike } = usePost();
  const [showComments, setShowComments] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [isLiking, setIsLiking] = useState(false);

  const isAuthor = user?._id === post.author?._id;
  const isLiked = post.likes?.includes(user?._id);
  const likesCount = post.likesCount || post.likes?.length || 0;
  const commentsCount = post.commentsCount || post.comments?.length || 0;

  const handleLike = async () => {
    if (isLiking) return;

    setIsLiking(true);
    try {
      await toggleLike(post._id);
    } catch (error) {
      console.error('Error toggling like:', error);
    } finally {
      setIsLiking(false);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        await deletePost(post._id);
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) {
      const diffHours = Math.ceil(diffTime / (1000 * 60 * 60));
      if (diffHours === 0) {
        const diffMinutes = Math.ceil(diffTime / (1000 * 60));
        return diffMinutes <= 1 ? 'Just now' : `${diffMinutes}m ago`;
      }
      return `${diffHours}h ago`;
    } else if (diffDays === 1) {
      return 'Yesterday';
    } else if (diffDays < 7) {
      return `${diffDays}d ago`;
    } else {
      return date.toLocaleDateString('en-US', { 
        month: 'short', 
        day: 'numeric',
        year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
      });
    }
  };

  return (
    <div className="glass rounded-2xl shadow-xl overflow-hidden card-hover fade-in">
      {/* Post Header */}
      <div className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex items-center space-x-4">
            <Link to={`/users/${post.author?._id}`}>
              <img
                src={post.author?.profileImage || 'https://via.placeholder.com/48/667eea/ffffff?text=U'}
                alt={post.author?.username}
                className="w-12 h-12 rounded-full object-cover hover:scale-110 transition-transform border-2 border-white/50"
              />
            </Link>
            <div>
              <Link 
                to={`/users/${post.author?._id}`}
                className="font-semibold text-gray-900 hover:text-purple-600 transition-colors"
              >
                {post.author?.username}
              </Link>
              <p className="text-sm text-gray-500">{formatDate(post.createdAt)}</p>
            </div>
          </div>

          {/* Dropdown Menu */}
          {isAuthor && (
            <div className="relative">
              <button
                onClick={() => setShowDropdown(!showDropdown)}
                className="p-2 text-gray-400 hover:text-gray-600 hover:bg-white/10 rounded-full transition-all duration-200"
              >
                <MoreHorizontal size={20} />
              </button>

              {showDropdown && (
                <div className="absolute right-0 mt-2 w-48 glass rounded-xl shadow-2xl border border-white/20 z-10 scale-in">
                  <div className="py-2">
                    <Link
                      to={`/posts/${post._id}/edit`}
                      className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-white/10 transition-colors"
                      onClick={() => setShowDropdown(false)}
                    >
                      <Edit size={16} className="mr-3" />
                      Edit Post
                    </Link>
                    <button
                      onClick={() => {
                        handleDelete();
                        setShowDropdown(false);
                      }}
                      className="flex items-center w-full px-4 py-2 text-sm text-red-600 hover:bg-red-50/50 transition-colors"
                    >
                      <Trash2 size={16} className="mr-3" />
                      Delete Post
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Post Content */}
      <div className="px-6 pb-4">
        <p className="text-gray-900 whitespace-pre-wrap break-words text-lg leading-relaxed">
          {post.content}
        </p>
      </div>

      {/* Post Image */}
      {post.image && (
        <div className="px-6 pb-4">
          <div className="relative group">
            <img
              src={post.image}
              alt="Post content"
              className="w-full rounded-xl object-cover max-h-96 transition-all duration-300 group-hover:scale-[1.02]"
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-xl"></div>
          </div>
        </div>
      )}

      {/* Post Actions */}
      <div className="px-6 py-4 border-t border-white/10">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-6">
            {/* Like Button */}
            <button
              onClick={handleLike}
              disabled={isLiking}
              className={`flex items-center space-x-2 group transition-all duration-300 ${
                isLiked 
                  ? 'text-red-500' 
                  : 'text-gray-500 hover:text-red-500'
              }`}
            >
              <div className={`p-2 rounded-full transition-all duration-300 ${
                isLiked 
                  ? 'bg-red-100 text-red-500' 
                  : 'hover:bg-red-100'
              }`}>
                <Heart 
                  size={20} 
                  className={isLiked ? 'fill-current animate-pulse' : ''} 
                />
              </div>
              <span className="font-medium">{likesCount}</span>
            </button>

            {/* Comment Button */}
            <button
              onClick={() => setShowComments(!showComments)}
              className="flex items-center space-x-2 text-gray-500 hover:text-purple-600 transition-all duration-300"
            >
              <div className="p-2 rounded-full hover:bg-purple-100 transition-colors">
                <MessageCircle size={20} />
              </div>
              <span className="font-medium">{commentsCount}</span>
            </button>

            {/* Share Button */}
            <button
              className="flex items-center space-x-2 text-gray-500 hover:text-purple-600 transition-all duration-300"
              onClick={() => {
                navigator.clipboard.writeText(window.location.origin + `/posts/${post._id}`);
                // Show toast notification
                const toast = document.createElement('div');
                toast.className = 'toast success fixed top-4 right-4 z-50';
                toast.textContent = 'Link copied to clipboard!';
                document.body.appendChild(toast);
                setTimeout(() => toast.remove(), 3000);
              }}
            >
              <div className="p-2 rounded-full hover:bg-purple-100 transition-colors">
                <Share2 size={20} />
              </div>
              <span className="font-medium">Share</span>
            </button>
          </div>
        </div>
      </div>

      {/* Comments Section */}
      {showComments && (
        <div className="border-t border-white/10">
          <CommentSection postId={post._id} />
        </div>
      )}
    </div>
  );
};

export default PostCard;
