import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { usePost } from '../context/PostContext';
import { Edit2, Camera, Settings, LogOut, User, Heart, MessageCircle, Grid3x3 } from 'lucide-react';
import { Card, Button, Badge, Form, Row, Col, Alert, Spinner } from 'react-bootstrap';
import PostCard from '../components/PostCard';

const Profile = () => {
  const { user, updateProfile, logout } = useAuth();
  const { posts } = usePost();
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({
    username: user?.username || '',
    bio: user?.bio || '',
    profileImage: user?.profileImage || ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showStats, setShowStats] = useState(true);

  const userPosts = posts.filter(post => post.author?._id === user?._id);

  const handleEditToggle = () => {
    if (isEditing) {
      // Reset form when canceling
      setEditForm({
        username: user?.username || '',
        bio: user?.bio || '',
        profileImage: user?.profileImage || ''
      });
    }
    setIsEditing(!isEditing);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const result = await updateProfile(editForm);
      if (result.success) {
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="min-h-screen">
      <div className="container-fluid px-4 py-4">
        <Row className="justify-content-center">
          <Col lg={8} xl={6}>
            {/* Profile Header Card */}
            <Card className="glass rounded-3 shadow-xl overflow-hidden mb-4 border-0 fade-in hover:shadow-2xl transition-all duration-300">
              {/* Cover Image */}
              <div className="h-32 bg-gradient-to-r from-purple-600 to-pink-600 position-relative">
                <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent"></div>
                <div className="absolute bottom-0 left-0 right-0 p-4">
                  <div className="d-flex align-items-end gap-4">
                    <div className="position-relative group">
                      <img
                        src={user?.profileImage || 'https://via.placeholder.com/100/3b82f6/ffffff?text=U'}
                        alt={user?.username}
                        className="w-24 h-24 rounded-full object-cover border-4 border-white shadow-lg hover:scale-110 transition-all duration-300 cursor-pointer"
                      />
                      <div className="absolute bottom-0 right-0 w-6 h-6 bg-green-500 rounded-full border-2 border-white d-flex align-items-center justify-center group-hover:scale-125 transition-transform">
                        <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                      </div>
                      <div className="absolute inset-0 w-24 h-24 rounded-full bg-gradient-to-r from-purple-400 to-pink-400 opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
                    </div>
                    <div className="text-white">
                      <h2 className="h3 mb-1 fw-bold hover:scale-105 transition-transform cursor-pointer">{user?.username}</h2>
                      <p className="text-white/80 small">Joined {formatDate(user?.createdAt)}</p>
                    </div>
                  </div>
                </div>
              </div>

              <Card.Body className="p-4">
                {/* Action Buttons */}
                <div className="d-flex justify-content-between align-items-center mb-4">
                  {isEditing ? (
                    <div className="d-flex gap-2">
                      <Button
                        onClick={handleSubmit}
                        disabled={isSubmitting}
                        className="btn-hover glow px-4 py-2 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300"
                        variant="primary"
                      >
                        {isSubmitting ? (
                          <>
                            <Spinner as="span" animation="border" size="sm" className="me-2" />
                            Saving...
                          </>
                        ) : (
                          'Save Changes'
                        )}
                      </Button>
                      <Button
                        onClick={handleEditToggle}
                        variant="outline-secondary"
                        className="px-4 py-2 rounded-xl hover:bg-gray-100 transition-all duration-300"
                      >
                        Cancel
                      </Button>
                    </div>
                  ) : (
                    <div className="d-flex gap-2">
                      <Button
                        onClick={handleEditToggle}
                        className="btn-hover px-4 py-2 rounded-xl shadow-md hover:shadow-lg transition-all duration-300"
                        variant="outline-primary"
                      >
                        <Edit2 size={16} className="me-2" />
                        Edit Profile
                      </Button>
                      <Button
                        variant="outline-secondary"
                        className="btn-hover px-3 py-2 rounded-xl shadow-md hover:shadow-lg transition-all duration-300"
                      >
                        <Settings size={16} />
                      </Button>
                    </div>
                  )}

                  <Button
                    onClick={logout}
                    variant="outline-danger"
                    className="btn-hover px-4 py-2 rounded-xl hover:bg-red-50 transition-all duration-300"
                  >
                    <LogOut size={16} className="me-2" />
                    Logout
                  </Button>
                </div>

                {/* Bio Section */}
                <div className="mb-4">
                  {isEditing ? (
                    <div className="space-y-3 slide-up">
                      <Form.Group>
                        <Form.Label className="form-label text-dark fw-semibold">Bio</Form.Label>
                        <Form.Control
                          as="textarea"
                          name="bio"
                          value={editForm.bio}
                          onChange={handleChange}
                          rows={3}
                          maxLength={500}
                          placeholder="Tell us about yourself..."
                          className="form-input border-2 rounded-xl shadow-sm focus:shadow-lg transition-all duration-300"
                        />
                        <div className="text-end mt-1">
                          <small className="text-muted">{editForm.bio.length}/500</small>
                        </div>
                      </Form.Group>
                      <Form.Group>
                        <Form.Label className="form-label text-dark fw-semibold">Profile Image URL</Form.Label>
                        <Form.Control
                          type="url"
                          name="profileImage"
                          value={editForm.profileImage}
                          onChange={handleChange}
                          placeholder="https://example.com/image.jpg"
                          className="form-input border-2 rounded-xl shadow-sm focus:shadow-lg transition-all duration-300"
                        />
                      </Form.Group>
                    </div>
                  ) : (
                    <div className="p-3 rounded-xl bg-white/50 backdrop-blur-sm border border-white/20 hover:bg-white/70 transition-all duration-300">
                      <h4 className="h5 mb-2 text-dark fw-bold">About</h4>
                      <p className="text-dark mb-0 lh-base">
                        {user?.bio || (
                          <span className="text-muted fst-italic">No bio yet. Click edit profile to add one!</span>
                        )}
                      </p>
                    </div>
                  )}
                </div>

                {/* Stats */}
                <div className="border-top border-white/10 pt-4">
                  <div className="d-flex justify-content-around text-center">
                    <div className="text-center p-3 rounded-xl bg-gradient-to-br from-purple-50 to-pink-50 hover:from-purple-100 hover:to-pink-100 transition-all duration-300 cursor-pointer group">
                      <div className="h3 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{userPosts.length}</div>
                      <div className="text-muted small fw-medium">Posts</div>
                    </div>
                    <div className="text-center p-3 rounded-xl bg-gradient-to-br from-blue-50 to-cyan-50 hover:from-blue-100 hover:to-cyan-100 transition-all duration-300 cursor-pointer group">
                      <div className="h3 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{user?.followersCount || 0}</div>
                      <div className="text-muted small fw-medium">Followers</div>
                    </div>
                    <div className="text-center p-3 rounded-xl bg-gradient-to-br from-green-50 to-emerald-50 hover:from-green-100 hover:to-emerald-100 transition-all duration-300 cursor-pointer group">
                      <div className="h3 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{user?.followingCount || 0}</div>
                      <div className="text-muted small fw-medium">Following</div>
                    </div>
                  </div>
                </div>
              </Card.Body>
            </Card>

            {/* User's Posts */}
            <div className="mt-5">
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h3 className="h4 fw-bold text-dark position-relative">
                  Your Posts
                  <span className="position-absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full scale-x-0 hover:scale-x-100 transition-transform duration-300"></span>
                </h3>
                <Badge bg="primary" text="white" pill className="px-3 py-2 shadow-lg">
                  {userPosts.length}
                </Badge>
              </div>

              {userPosts.length === 0 ? (
                <Card className="text-center p-5 border-0 glass hover:shadow-2xl transition-all duration-300 transform hover:scale-[1.02]">
                  <Card.Body>
                    <div className="text-center mb-4">
                      <div className="w-20 h-20 mx-auto mb-4 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl d-flex align-items-center justify-center group-hover:scale-110 transition-transform">
                        <Grid3x3 className="w-10 h-10 text-purple-600 group-hover:text-purple-700 transition-colors" />
                      </div>
                      <h4 className="h5 text-dark mb-2 fw-bold">No posts yet</h4>
                      <p className="text-muted mb-4">
                        Your posts will appear here when you create them.
                      </p>
                      <Button variant="primary" className="btn-hover glow px-4 py-2 rounded-xl">
                        Create Your First Post
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
              ) : (
                <div className="space-y-4">
                  {userPosts.map((post, index) => (
                    <div key={post._id} className="fade-in hover:scale-[1.01] transition-transform duration-300" style={{ animationDelay: `${index * 0.1}s` }}>
                      <PostCard post={post} />
                    </div>
                  ))}
                </div>
              )}
            </div>
          </Col>
        </Row>
      </div>
    </div>
  );
};

export default Profile;
