import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { Search, User, Home, LogOut, Menu, X, Sparkles } from 'lucide-react';
import { Navbar as BSNavbar, Nav, Container, NavDropdown, Button } from 'react-bootstrap';

const CustomNavbar = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
    setIsMenuOpen(false);
  };

  const isActivePath = (path) => {
    return location.pathname === path;
  };

  return (
    <BSNavbar 
      expand="lg" 
      className="glass sticky-top shadow-xl border-0 px-4"
      variant="light"
    >
      <Container fluid="xl">
        <BSNavbar.Brand 
          as={Link} 
          to="/" 
          className="d-flex align-items-center text-white font-bold text-xl text-decoration-none hover:scale-105 transition-transform"
        >
          <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl d-flex align-items-center justify-center glow floating me-3">
            <Sparkles className="w-6 h-6 text-white" />
          </div>
          <span className="neon-text">SocialHub</span>
        </BSNavbar.Brand>

        <BSNavbar.Toggle 
          aria-controls="basic-navbar-nav" 
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          className="border-0 text-white hover:bg-white/10"
        >
          {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </BSNavbar.Toggle>

        <BSNavbar.Collapse id="basic-navbar-nav">
          {isAuthenticated && (
            <Nav className="me-auto">
              <Nav.Link 
                as={Link} 
                to="/"
                className={`text-white d-flex align-items-center px-3 py-2 rounded-xl transition-all duration-300 ${
                  isActivePath('/') 
                    ? 'bg-white/20 text-white backdrop-blur-sm' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <Home size={18} className="me-2" />
                Home
              </Nav.Link>

              <Nav.Link 
                as={Link} 
                to="/search"
                className={`text-white d-flex align-items-center px-3 py-2 rounded-xl transition-all duration-300 ${
                  isActivePath('/search') 
                    ? 'bg-white/20 text-white backdrop-blur-sm' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <Search size={18} className="me-2" />
                Search
              </Nav.Link>

              <Nav.Link 
                as={Link} 
                to="/profile"
                className={`text-white d-flex align-items-center px-3 py-2 rounded-xl transition-all duration-300 ${
                  isActivePath('/profile') 
                    ? 'bg-white/20 text-white backdrop-blur-sm' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <User size={18} className="me-2" />
                Profile
              </Nav.Link>
            </Nav>
          )}

          <Nav className="align-items-center">
            {isAuthenticated ? (
              <>
                <div className="glass px-4 py-2 rounded-xl d-flex align-items-center me-3">
                  <img
                    src={user?.profileImage || 'https://via.placeholder.com/32/667eea/ffffff?text=U'}
                    alt={user?.username}
                    className="w-8 h-8 rounded-full object-cover border-2 border-white/50 hover:scale-110 transition-transform me-2"
                  />
                  <span className="text-sm font-medium text-white">{user?.username}</span>
                </div>

                <Button 
                  variant="outline-light" 
                  onClick={handleLogout}
                  className="d-flex align-items-center px-3 py-2 text-sm text-white/80 hover:text-white border-white/30 hover:bg-white/10 rounded-xl transition-all duration-300"
                >
                  <LogOut size={18} className="me-2" />
                  Logout
                </Button>
              </>
            ) : (
              <>
                <Button 
                  as={Link}
                  to="/login"
                  variant="outline-light"
                  className="px-4 py-2 text-sm text-white/80 hover:text-white border-white/30 me-2"
                >
                  Login
                </Button>
                <Button 
                  as={Link}
                  to="/register"
                  className="px-4 py-2 text-sm font-medium text-white bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 border-0 rounded-xl transition-all duration-300 btn-hover glow"
                >
                  Sign Up
                </Button>
              </>
            )}
          </Nav>
        </BSNavbar.Collapse>
      </Container>
    </BSNavbar>
  );
};

export default CustomNavbar;
