import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Search as SearchIcon, Users, Loader2, Sparkles, UserCheck } from 'lucide-react';
import { Card, Form, Button, Badge, Spinner } from 'react-bootstrap';

const Search = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (query.trim()) {
        handleSearch();
      } else {
        setResults([]);
        setSearched(false);
      }
    }, 500);

    return () => clearTimeout(timer);
  }, [query]);

  const handleSearch = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setSearched(true);

    try {
      const response = await fetch(`http://localhost:5000/api/users?search=${encodeURIComponent(query.trim())}`);
      const data = await response.json();

      if (data.success) {
        setResults(data.data.users);
      }
    } catch (error) {
      console.error('Error searching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSearch();
  };

  return (
    <div className="min-h-screen">
      <div className="container-fluid px-4 py-4">
        {/* Header Section */}
        <Card className="glass rounded-3 shadow-xl mb-4 border-0 fade-in">
          <Card.Body className="p-4">
            <div className="d-flex align-items-center">
              <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl d-flex align-items-center justify-center glow me-3 floating">
                <SearchIcon className="w-6 h-6 text-white" />
              </div>
              <h1 className="h2 mb-0 fw-bold text-dark position-relative">
                Search Users
                <span className="position-absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full scale-x-0 hover:scale-x-100 transition-transform duration-300"></span>
              </h1>
            </div>
          </Card.Body>
        </Card>

        {/* Search Form */}
        <Card className="glass rounded-3 shadow-xl mb-4 border-0">
          <Card.Body className="p-4">
            <Form onSubmit={handleSubmit}>
              <Form.Group>
                <div className="position-relative">
                  <div className="position-absolute top-50 start-0 translate-middle-y ps-3">
                    <SearchIcon className="h-5 w-5 text-muted" />
                  </div>
                  <Form.Control
                    type="text"
                    value={query}
                    onChange={(e) => setQuery(e.target.value)}
                    placeholder="Search by username or bio..."
                    className="ps-5 pe-5 border-2 rounded-xl shadow-sm focus:shadow-lg transition-all duration-300"
                    autoFocus
                  />
                  {query && (
                    <div className="position-absolute top-50 end-0 translate-middle-y pe-3">
                      <Sparkles className="h-4 w-4 text-purple-500 animate-pulse" />
                    </div>
                  )}
                </div>
              </Form.Group>
            </Form>
          </Card.Body>
        </Card>

        {/* Loading State */}
        {loading && (
          <Card className="text-center p-5 border-0 glass mb-4">
            <Card.Body>
              <div className="text-center">
                <div className="w-16 h-16 mx-auto mb-4 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl d-flex align-items-center justify-center">
                  <Loader2 className="w-8 h-8 text-purple-600 animate-spin" />
                </div>
                <h3 className="h5 text-dark mb-2 fw-bold">Searching Users</h3>
                <p className="text-muted">Finding people matching your search...</p>
              </div>
            </Card.Body>
          </Card>
        )}

        {/* Search Results */}
        {!loading && searched && (
          <div>
            {results.length === 0 ? (
              <Card className="text-center p-5 border-0 glass hover:shadow-2xl transition-all duration-300">
                <Card.Body>
                  <div className="text-center">
                    <div className="w-20 h-20 mx-auto mb-4 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl d-flex align-items-center justify-center group-hover:scale-110 transition-transform">
                      <Users className="w-10 h-10 text-gray-400 group-hover:text-gray-600 transition-colors" />
                    </div>
                    <h3 className="h4 text-dark mb-2 fw-bold">
                      {query.trim() ? 'No Users Found' : 'Start Searching'}
                    </h3>
                    <p className="text-muted mb-4">
                      {query.trim() 
                        ? `No users found matching "${query.trim()}". Try different keywords.`
                        : 'Enter a username or bio keywords to search for users.'
                      }
                    </p>
                    <Badge bg="secondary" text="white" pill className="px-3 py-2">
                      <UserCheck className="w-4 h-4 me-2" />
                      Try searching for friends
                    </Badge>
                  </div>
                </Card.Body>
              </Card>
            ) : (
              <div>
                <div className="text-center mb-4">
                  <Badge bg="success" text="white" pill className="px-4 py-2 shadow-lg">
                    <Sparkles className="w-4 h-4 me-2" />
                    Found {results.length} user{results.length !== 1 ? 's' : ''} 
                    {query.trim() && ` matching "${query.trim()}"`}
                  </Badge>
                </div>

                <div className="row g-4">
                  {results.map((user, index) => (
                    <div key={user._id} className="col-md-6 col-lg-4">
                      <Card className="glass rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 hover:scale-[1.02] border-0 fade-in" style={{ animationDelay: `${index * 0.1}s` }}>
                        <Card.Body className="p-4">
                          <div className="d-flex align-items-center justify-content-between mb-3">
                            <div className="d-flex align-items-center">
                              <Link to={`/users/${user._id}`}>
                                <img
                                  src={user.profileImage || 'https://via.placeholder.com/48/3b82f6/ffffff?text=U'}
                                  alt={user.username}
                                  className="w-12 h-12 rounded-full object-cover border-2 border-white/50 hover:scale-110 transition-transform"
                                />
                              </Link>
                            </div>

                            <div className="text-end">
                              <Badge bg="primary" text="white" pill className="px-2 py-1">
                                Active
                              </Badge>
                            </div>
                          </div>

                          <div className="mb-3">
                            <Link
                              to={`/users/${user._id}`}
                              className="text-decoration-none"
                            >
                              <h5 className="fw-bold text-dark mb-1 hover:text-purple-600 transition-colors">
                                {user.username}
                              </h5>
                            </Link>
                            <p className="text-muted small mb-2 lh-base">
                              {user.bio || 'No bio available'}
                            </p>
                          </div>

                          <div className="d-flex justify-content-between align-items-center">
                            <div className="d-flex gap-3">
                              <Badge bg="outline-primary" text="primary" pill className="px-2 py-1">
                                <Users className="w-3 h-3 me-1" />
                                {user.followersCount || 0} followers
                              </Badge>
                              <Badge bg="outline-secondary" text="secondary" pill className="px-2 py-1">
                                {user.followingCount || 0} following
                              </Badge>
                            </div>

                            <Button
                              as={Link}
                              to={`/users/${user._id}`}
                              className="btn-hover px-3 py-1 rounded-xl"
                              variant="primary"
                              size="sm"
                            >
                              View Profile
                            </Button>
                          </div>
                        </Card.Body>
                      </Card>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}

        {/* Search Tips */}
        {!searched && !loading && (
          <Card className="glass rounded-3 shadow-xl border-0">
            <Card.Body className="p-4">
              <div className="d-flex align-items-center mb-3">
                <div className="w-8 h-8 bg-gradient-to-r from-blue-500 to-cyan-500 rounded-xl d-flex align-items-center justify-center me-3">
                  <Sparkles className="w-4 h-4 text-white" />
                </div>
                <h4 className="h5 fw-bold text-dark mb-0">Search Tips</h4>
              </div>
              <div className="row g-3">
                <div className="col-md-6">
                  <div className="d-flex align-items-start p-3 rounded-xl bg-blue-50 hover:bg-blue-100 transition-colors">
                    <span className="text-blue-600 me-2">•</span>
                    <span className="text-dark small">Search by username to find specific users</span>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="d-flex align-items-start p-3 rounded-xl bg-purple-50 hover:bg-purple-100 transition-colors">
                    <span className="text-purple-600 me-2">•</span>
                    <span className="text-dark small">Use keywords to search user bios</span>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="d-flex align-items-start p-3 rounded-xl bg-green-50 hover:bg-green-100 transition-colors">
                    <span className="text-green-600 me-2">•</span>
                    <span className="text-dark small">Search is case-insensitive</span>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="d-flex align-items-start p-3 rounded-xl bg-pink-50 hover:bg-pink-100 transition-colors">
                    <span className="text-pink-600 me-2">•</span>
                    <span className="text-dark small">Results update automatically as you type</span>
                  </div>
                </div>
              </div>
            </Card.Body>
          </Card>
        )}
      </div>
    </div>
  );
};

export default Search;
