import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { Search, User, Home, LogOut, Menu, X, Sparkles } from 'lucide-react';

const Navbar = () => {
  const { user, isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
    setIsMenuOpen(false);
  };

  const isActivePath = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="glass sticky top-0 z-50 border-0 shadow-xl">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <Link 
            to="/" 
            className="flex items-center space-x-3 text-white font-bold text-xl hover:scale-105 transition-transform"
          >
            <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl flex items-center justify-center glow floating">
              <Sparkles className="w-6 h-6 text-white" />
            </div>
            <span className="neon-text">SocialHub</span>
          </Link>

          {/* Desktop Navigation */}
          {isAuthenticated && (
            <div className="hidden md:flex items-center space-x-2">
              <Link
                to="/"
                className={`flex items-center space-x-2 px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 ${
                  isActivePath('/') 
                    ? 'bg-white/20 text-white backdrop-blur-sm' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <Home size={18} />
                <span>Home</span>
              </Link>

              <Link
                to="/search"
                className={`flex items-center space-x-2 px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 ${
                  isActivePath('/search') 
                    ? 'bg-white/20 text-white backdrop-blur-sm' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <Search size={18} />
                <span>Search</span>
              </Link>

              <Link
                to="/profile"
                className={`flex items-center space-x-2 px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 ${
                  isActivePath('/profile') 
                    ? 'bg-white/20 text-white backdrop-blur-sm' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <User size={18} />
                <span>Profile</span>
              </Link>
            </div>
          )}

          {/* User Menu - Desktop */}
          {isAuthenticated && (
            <div className="hidden md:flex items-center space-x-4">
              <div className="flex items-center space-x-3 glass px-4 py-2 rounded-xl">
                <img
                  src={user?.profileImage || 'https://via.placeholder.com/32/667eea/ffffff?text=U'}
                  alt={user?.username}
                  className="w-8 h-8 rounded-full object-cover border-2 border-white/50 hover:scale-110 transition-transform"
                />
                <span className="text-sm font-medium text-white">{user?.username}</span>
              </div>

              <button
                onClick={handleLogout}
                className="flex items-center space-x-2 px-4 py-2 text-sm text-white/80 hover:text-white hover:bg-white/10 rounded-xl transition-all duration-300"
              >
                <LogOut size={18} />
                <span>Logout</span>
              </button>
            </div>
          )}

          {/* Mobile menu button */}
          {isAuthenticated && (
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="md:hidden p-2 rounded-xl text-white/80 hover:text-white hover:bg-white/10 transition-all duration-300"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          )}

          {/* Auth buttons - Desktop */}
          {!isAuthenticated && (
            <div className="hidden md:flex items-center space-x-4">
              <Link
                to="/login"
                className="px-6 py-2 text-sm font-medium text-white/80 hover:text-white transition-colors"
              >
                Login
              </Link>
              <Link
                to="/register"
                className="px-6 py-2 text-sm font-medium text-white bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 rounded-xl transition-all duration-300 btn-hover glow"
              >
                Sign Up
              </Link>
            </div>
          )}
        </div>

        {/* Mobile Navigation */}
        {isAuthenticated && isMenuOpen && (
          <div className="md:hidden glass-dark border-t border-white/10 py-4 slide-up">
            <div className="flex flex-col space-y-2">
              <Link
                to="/"
                onClick={() => setIsMenuOpen(false)}
                className={`flex items-center space-x-3 px-4 py-3 rounded-xl text-sm font-medium transition-all duration-300 ${
                  isActivePath('/') 
                    ? 'bg-white/20 text-white' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <Home size={18} />
                <span>Home</span>
              </Link>

              <Link
                to="/search"
                onClick={() => setIsMenuOpen(false)}
                className={`flex items-center space-x-3 px-4 py-3 rounded-xl text-sm font-medium transition-all duration-300 ${
                  isActivePath('/search') 
                    ? 'bg-white/20 text-white' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <Search size={18} />
                <span>Search</span>
              </Link>

              <Link
                to="/profile"
                onClick={() => setIsMenuOpen(false)}
                className={`flex items-center space-x-3 px-4 py-3 rounded-xl text-sm font-medium transition-all duration-300 ${
                  isActivePath('/profile') 
                    ? 'bg-white/20 text-white' 
                    : 'text-white/80 hover:text-white hover:bg-white/10'
                }`}
              >
                <User size={18} />
                <span>Profile</span>
              </Link>

              <div className="border-t border-white/10 pt-2 mt-2">
                <div className="flex items-center space-x-3 px-4 py-3">
                  <img
                    src={user?.profileImage || 'https://via.placeholder.com/32/667eea/ffffff?text=U'}
                    alt={user?.username}
                    className="w-8 h-8 rounded-full object-cover border-2 border-white/50"
                  />
                  <span className="text-sm font-medium text-white">{user?.username}</span>
                </div>

                <button
                  onClick={handleLogout}
                  className="flex items-center space-x-3 px-4 py-3 w-full text-sm text-white/80 hover:text-white hover:bg-white/10 rounded-xl transition-all duration-300"
                >
                  <LogOut size={18} />
                  <span>Logout</span>
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Mobile Auth buttons */}
        {!isAuthenticated && (
          <div className="md:hidden glass-dark border-t border-white/10 py-4">
            <div className="flex flex-col space-y-3">
              <Link
                to="/login"
                className="px-6 py-3 text-center text-sm font-medium text-white/80 hover:text-white transition-colors"
              >
                Login
              </Link>
              <Link
                to="/register"
                className="px-6 py-3 text-center text-sm font-medium text-white bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 rounded-xl transition-all duration-300 btn-hover glow"
              >
                Sign Up
              </Link>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
