import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { Eye, EyeOff, Mail, Lock, User, AlertCircle, Sparkles } from 'lucide-react';
import { Card, Form, Button, Alert as BSAlert } from 'react-bootstrap';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const { register, isAuthenticated, error, clearError } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/');
    }
  }, [isAuthenticated, navigate]);

  useEffect(() => {
    if (error) {
      setErrors({ general: error });
      clearError();
    }
  }, [error, clearError]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear field-specific error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.username) {
      newErrors.username = 'Username is required';
    } else if (formData.username.length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    } else if (formData.username.length > 30) {
      newErrors.username = 'Username cannot exceed 30 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      newErrors.username = 'Username can only contain letters, numbers, and underscores';
    }

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const { confirmPassword, ...registerData } = formData;
      const result = await register(registerData);

      if (!result.success) {
        setErrors({ general: result.message });
      }
    } catch (error) {
      setErrors({ general: 'An unexpected error occurred' });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen d-flex align-items-center justify-content-center px-4">
      <div className="absolute inset-0 bg-gradient-to-br from-purple-600/20 to-pink-600/20 backdrop-blur-3xl"></div>

      <div className="relative w-100" style={{ maxWidth: '450px' }}>
        {/* Background decoration */}
        <div className="absolute -top-20 -left-20 w-40 h-40 bg-purple-500/30 rounded-full blur-3xl floating"></div>
        <div className="absolute -bottom-20 -right-20 w-40 h-40 bg-pink-500/30 rounded-full blur-3xl floating" style={{ animationDelay: '1s' }}></div>

        <Card className="glass rounded-2xl shadow-2xl p-4 scale-in border-0">
          <Card.Body className="p-4">
            <div className="text-center mb-4">
              <div className="mx-auto w-16 h-16 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl d-flex align-items-center justify-center glow mb-3 floating">
                <Sparkles className="w-8 h-8 text-white" />
              </div>
              <h2 className="fw-bold mb-2">
                Create Account
              </h2>
              <p className="text-muted">
                Join our community and start sharing your thoughts
              </p>
            </div>

            {errors.general && (
              <BSAlert variant="danger" className="mb-4 slide-up">
                <div className="d-flex align-items-center">
                  <AlertCircle className="h-5 w-5 text-danger me-2" />
                  <span className="text-sm">{errors.general}</span>
                </div>
              </BSAlert>
            )}

            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-4">
                <Form.Label className="form-label">Username</Form.Label>
                <div className="position-relative">
                  <div className="position-absolute top-50 start-0 translate-middle-y ps-3">
                    <User className="h-5 w-5 text-muted" />
                  </div>
                  <Form.Control
                    type="text"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    placeholder="Choose a username"
                    className={`ps-5 ${errors.username ? 'is-invalid' : ''}`}
                    isInvalid={!!errors.username}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.username}
                  </Form.Control.Feedback>
                </div>
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label className="form-label">Email address</Form.Label>
                <div className="position-relative">
                  <div className="position-absolute top-50 start-0 translate-middle-y ps-3">
                    <Mail className="h-5 w-5 text-muted" />
                  </div>
                  <Form.Control
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Enter your email"
                    className={`ps-5 ${errors.email ? 'is-invalid' : ''}`}
                    isInvalid={!!errors.email}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.email}
                  </Form.Control.Feedback>
                </div>
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label className="form-label">Password</Form.Label>
                <div className="position-relative">
                  <div className="position-absolute top-50 start-0 translate-middle-y ps-3">
                    <Lock className="h-5 w-5 text-muted" />
                  </div>
                  <Form.Control
                    type={showPassword ? 'text' : 'password'}
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Create a password"
                    className={`ps-5 pe-5 ${errors.password ? 'is-invalid' : ''}`}
                    isInvalid={!!errors.password}
                  />
                  <Button
                    variant="link"
                    className="position-absolute top-50 end-0 translate-middle-y pe-3 text-muted"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? (
                      <EyeOff className="h-5 w-5" />
                    ) : (
                      <Eye className="h-5 w-5" />
                    )}
                  </Button>
                  <Form.Control.Feedback type="invalid">
                    {errors.password}
                  </Form.Control.Feedback>
                </div>
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label className="form-label">Confirm Password</Form.Label>
                <div className="position-relative">
                  <div className="position-absolute top-50 start-0 translate-middle-y ps-3">
                    <Lock className="h-5 w-5 text-muted" />
                  </div>
                  <Form.Control
                    type={showConfirmPassword ? 'text' : 'password'}
                    name="confirmPassword"
                    value={formData.confirmPassword}
                    onChange={handleChange}
                    placeholder="Confirm your password"
                    className={`ps-5 pe-5 ${errors.confirmPassword ? 'is-invalid' : ''}`}
                    isInvalid={!!errors.confirmPassword}
                  />
                  <Button
                    variant="link"
                    className="position-absolute top-50 end-0 translate-middle-y pe-3 text-muted"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  >
                    {showConfirmPassword ? (
                      <EyeOff className="h-5 w-5" />
                    ) : (
                      <Eye className="h-5 w-5" />
                    )}
                  </Button>
                  <Form.Control.Feedback type="invalid">
                    {errors.confirmPassword}
                  </Form.Control.Feedback>
                </div>
              </Form.Group>

              <Button
                type="submit"
                disabled={isSubmitting}
                className="w-100 py-3 btn-hover glow"
                variant="primary"
              >
                {isSubmitting ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Creating Account...
                  </>
                ) : (
                  'Create Account'
                )}
              </Button>

              <div className="text-center mt-4">
                <Link
                  to="/login"
                  className="text-purple-600 text-decoration-none fw-medium"
                >
                  Already have an account? Sign in
                </Link>
              </div>
            </Form>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default Register;
