import React, { useState } from 'react';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { Image, Send, X, Sparkles } from 'lucide-react';
import { Card, Form, Button, InputGroup } from 'react-bootstrap';

const CreatePost = () => {
  const [content, setContent] = useState('');
  const [image, setImage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showImageInput, setShowImageInput] = useState(false);

  const { createPost, error, clearError } = usePost();
  const { user } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!content.trim()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const result = await createPost({
        content: content.trim(),
        image: image.trim() || null
      });

      if (result.success) {
        setContent('');
        setImage('');
        setShowImageInput(false);
      }
    } catch (error) {
      console.error('Error creating post:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleImageToggle = () => {
    setShowImageInput(!showImageInput);
    if (!showImageInput) {
      setImage('');
    }
  };

  const handleImageRemove = () => {
    setImage('');
    setShowImageInput(false);
  };

  const charCount = content.length;
  const maxChars = 2000;
  const isNearLimit = charCount > maxChars * 0.9;
  const isAtLimit = charCount >= maxChars;

  return (
    <Card className="glass rounded-2xl shadow-xl p-4 mb-4 fade-in border-0">
      <Card.Body>
        <Form onSubmit={handleSubmit}>
          <div className="d-flex gap-3 mb-3">
            <div className="position-relative">
              <img
                src={user?.profileImage || 'https://via.placeholder.com/48/667eea/ffffff?text=U'}
                alt={user?.username}
                className="w-12 h-12 rounded-full object-cover border-2 border-white/50"
              />
              <div className="position-absolute bottom-0 end-0 w-4 h-4 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full border-2 border-white"></div>
            </div>

            <div className="flex-grow">
              <Form.Control
                as="textarea"
                value={content}
                onChange={(e) => setContent(e.target.value)}
                placeholder="Share your thoughts with the world..."
                className="border-0 bg-transparent text-lg resize-none"
                rows={3}
                maxLength={maxChars}
                disabled={isSubmitting}
              />

              {/* Image Preview */}
              {image && (
                <div className="mt-3 position-relative">
                  <img
                    src={image}
                    alt="Post image"
                    className="w-100 rounded-xl object-cover shadow-lg transition-all duration-300 hover:scale-[1.02]"
                    style={{ maxWidth: '400px' }}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      setImage('');
                    }}
                  />
                  <Button
                    variant="danger"
                    size="sm"
                    onClick={handleImageRemove}
                    className="position-absolute top-2 end-2 bg-danger/80 text-white rounded-circle p-1 hover:bg-danger transition-all duration-200"
                  >
                    <X size={16} />
                  </Button>
                </div>
              )}

              {/* Image Input */}
              {showImageInput && !image && (
                <div className="mt-3 slide-up">
                  <Form.Control
                    type="url"
                    value={image}
                    onChange={(e) => setImage(e.target.value)}
                    placeholder="Enter image URL (optional)"
                    className="bg-white/80 backdrop-blur-sm"
                    disabled={isSubmitting}
                  />
                </div>
              )}
            </div>
          </div>

          {/* Actions Bar */}
          <div className="d-flex justify-content-between align-items-center mt-3 pt-3 border-top border-white/10">
            <div className="d-flex align-items-center gap-2">
              <Button
                variant="link"
                onClick={handleImageToggle}
                disabled={isSubmitting}
                className={`d-flex align-items-center text-decoration-none ${
                  showImageInput || image
                    ? 'text-purple-600' 
                    : 'text-muted hover:text-purple-600'
                }`}
              >
                <div className={`p-1.5 rounded-lg transition-colors ${
                  showImageInput || image ? 'bg-purple-100' : 'hover:bg-purple-100'
                }`}>
                  <Image size={18} />
                </div>
                <span className="small">{image ? 'Image Added' : 'Add Image'}</span>
              </Button>
            </div>

            <div className="d-flex align-items-center gap-3">
              {/* Character Count */}
              <div className="d-flex align-items-center">
                <span className={`small fw-medium ${
                  isAtLimit ? 'text-danger' : isNearLimit ? 'text-warning' : 'text-muted'
                }`}>
                  {charCount}/{maxChars}
                </span>
                {isNearLimit && (
                  <div className={`w-2 h-2 rounded-circle animate-pulse ${
                    isAtLimit ? 'bg-danger' : 'bg-warning'
                  }`}></div>
                )}
              </div>

              {/* Submit Button */}
              <Button
                type="submit"
                disabled={!content.trim() || isSubmitting || isAtLimit}
                className="btn-hover glow px-4 py-2"
                variant="primary"
              >
                {isSubmitting ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Posting...
                  </>
                ) : (
                  <>
                    <Send size={16} className="me-2" />
                    Post
                  </>
                )}
              </Button>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="mt-3 glass-dark border border-danger/50 rounded-xl p-3 slide-up">
              <div className="d-flex justify-content-between align-items-center">
                <div className="d-flex align-items-center">
                  <span className="small text-danger">{error}</span>
                </div>
                <Button
                  variant="link"
                  onClick={clearError}
                  className="text-danger p-0 text-decoration-none"
                >
                  <X size={16} />
                </Button>
              </div>
            </div>
          )}
        </Form>
      </Card.Body>
    </Card>
  );
};

export default CreatePost;
