import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import PostCard from '../components/PostCard';
import { UserPlus, UserMinus, Loader2, Calendar, MapPin, Link2, Sparkles } from 'lucide-react';
import { Card, Button, Badge, Spinner, Alert } from 'react-bootstrap';

const UserProfile = () => {
  const { userId } = useParams();
  const { user } = useAuth();
  const [profileData, setProfileData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isFollowing, setIsFollowing] = useState(false);
  const [followLoading, setFollowLoading] = useState(false);

  useEffect(() => {
    fetchUserProfile();
  }, [userId]);

  const fetchUserProfile = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/users/${userId}`);
      const data = await response.json();

      if (data.success) {
        setProfileData(data.data);
        setIsFollowing(data.data.isFollowing);
      } else {
        setError(data.message);
      }
    } catch (error) {
      setError('Failed to fetch user profile');
    } finally {
      setLoading(false);
    }
  };

  const handleFollowToggle = async () => {
    if (!user) return;

    setFollowLoading(true);
    try {
      const method = isFollowing ? 'DELETE' : 'POST';
      const response = await fetch(`http://localhost:5000/api/follows/${userId}`, {
        method,
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      const data = await response.json();

      if (data.success) {
        setIsFollowing(data.data.isFollowing);
        setProfileData(prev => ({
          ...prev,
          user: {
            ...prev.user,
            followersCount: data.data.followersCount
          }
        }));
      } else {
        setError(data.message);
      }
    } catch (error) {
      setError('Failed to update follow status');
    } finally {
      setFollowLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Card className="text-center p-5 border-0 glass">
          <Card.Body>
            <div className="text-center">
              <div className="w-16 h-16 mx-auto mb-4 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl d-flex align-items-center justify-center">
                <Loader2 className="w-8 h-8 text-purple-600 animate-spin" />
              </div>
              <h3 className="h5 text-dark mb-2 fw-bold">Loading Profile</h3>
              <p className="text-muted">Please wait while we fetch the user data...</p>
            </div>
          </Card.Body>
        </Card>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Alert variant="danger" className="fade-in">
          <div className="d-flex align-items-center">
            <Sparkles className="w-5 h-5 text-danger me-2" />
            <span className="text-danger">{error}</span>
          </div>
        </Alert>
      </div>
    );
  }

  if (!profileData) {
    return (
      <div className="min-h-screen d-flex align-items-center justify-content-center">
        <Card className="text-center p-5 border-0 glass hover:shadow-2xl transition-all duration-300">
          <Card.Body>
            <div className="text-center">
              <div className="w-20 h-20 mx-auto mb-4 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl d-flex align-items-center justify-center group-hover:scale-110 transition-transform">
                <UserMinus className="w-10 h-10 text-gray-400 group-hover:text-gray-600 transition-colors" />
              </div>
              <h3 className="h4 text-dark mb-2 fw-bold">User Not Found</h3>
              <p className="text-muted mb-4">The user you're looking for doesn't exist.</p>
              <Badge bg="secondary" text="white" pill className="px-3 py-2">
                <Link2 className="w-4 h-4 me-2" />
                Check the username and try again
              </Badge>
            </div>
          </Card.Body>
        </Card>
      </div>
    );
  }

  const { user: profileUser, posts } = profileData;
  const isOwnProfile = user?._id === profileUser._id;

  return (
    <div className="min-h-screen">
      <div className="container-fluid px-4 py-4">
        {/* Profile Header */}
        <Card className="glass rounded-3 shadow-xl overflow-hidden mb-4 border-0 fade-in">
          {/* Cover Image */}
          <div className="h-32 bg-gradient-to-r from-purple-600 to-pink-600 position-relative">
            <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent"></div>
            <div className="absolute bottom-0 left-0 right-0 p-4">
              <div className="d-flex align-items-end gap-4">
                <div className="position-relative group">
                  <img
                    src={profileUser.profileImage || 'https://via.placeholder.com/100/3b82f6/ffffff?text=U'}
                    alt={profileUser.username}
                    className="w-24 h-24 rounded-full object-cover border-4 border-white shadow-lg hover:scale-110 transition-all duration-300"
                  />
                  <div className="absolute inset-0 w-24 h-24 rounded-full bg-gradient-to-r from-purple-400 to-pink-400 opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
                </div>
                <div className="text-white">
                  <h2 className="h3 mb-1 fw-bold hover:scale-105 transition-transform cursor-pointer">{profileUser.username}</h2>
                  <div className="d-flex align-items-center gap-2">
                    <Calendar className="w-4 h-4 me-1" />
                    <p className="text-white/80 small">Joined {formatDate(profileUser.createdAt)}</p>
                  </div>
                </div>
              </div>
            </div>
            </div>
          </Card>

          <Card>
          <Card.Body className="p-4">
            {/* Action Buttons */}
            <div className="d-flex justify-content-between align-items-center mb-4">
              {!isOwnProfile && (
                <Button
                  onClick={handleFollowToggle}
                  disabled={followLoading}
                  className={`btn-hover px-4 py-2 rounded-xl shadow-md hover:shadow-lg transition-all duration-300 ${
                    isFollowing 
                      ? 'btn-secondary' 
                      : 'btn-primary'
                  }`}
                >
                  {followLoading ? (
                    <Spinner as="span" animation="border" size="sm" className="me-2" />
                  ) : isFollowing ? (
                    <>
                      <UserMinus size={16} className="me-2" />
                      Unfollow
                    </>
                  ) : (
                    <>
                      <UserPlus size={16} className="me-2" />
                      Follow
                    </>
                  )}
                </Button>
              )}

              {isOwnProfile && (
                <Button
                  as={Link}
                  to="/profile"
                  className="btn-hover px-4 py-2 rounded-xl shadow-md hover:shadow-lg transition-all duration-300"
                  variant="outline-primary"
                >
                  <Sparkles className="w-4 h-4 me-2" />
                  Edit Profile
                </Button>
              )}
            </div>

            {/* Bio Section */}
            <div className="mb-4">
              <div className="d-flex align-items-center mb-2">
                <MapPin className="w-4 h-4 text-muted me-2" />
                <h4 className="h5 mb-0 fw-bold text-dark">About</h4>
              </div>
              <div className="p-3 rounded-xl bg-white/50 backdrop-blur-sm border border-white/20 hover:bg-white/70 transition-all duration-300">
                <p className="text-dark mb-0 lh-base">
                  {profileUser.bio || (
                    <span className="text-muted fst-italic">No bio available.</span>
                  )}
                </p>
              </div>
            </div>

            {/* Stats */}
            <div className="border-top border-white/10 pt-4">
              <div className="d-flex justify-content-around text-center">
                <div className="text-center p-3 rounded-xl bg-gradient-to-br from-purple-50 to-pink-50 hover:from-purple-100 hover:to-pink-100 transition-all duration-300 cursor-pointer group">
                  <div className="h3 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{posts.length}</div>
                  <div className="text-muted small fw-medium">Posts</div>
                </div>
                <div className="text-center p-3 rounded-xl bg-gradient-to-br from-blue-50 to-cyan-50 hover:from-blue-100 hover:to-cyan-100 transition-all duration-300 cursor-pointer group">
                  <div className="h3 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{profileUser.followersCount}</div>
                  <div className="text-muted small fw-medium">Followers</div>
                </div>
                <div className="text-center p-3 rounded-xl bg-gradient-to-br from-green-50 to-emerald-50 hover:from-green-100 hover:to-emerald-100 transition-all duration-300 cursor-pointer group">
                  <div className="h3 fw-bold text-dark mb-1 group-hover:scale-110 transition-transform">{profileUser.followingCount}</div>
                  <div className="text-muted small fw-medium">Following</div>
                </div>
              </div>
            </div>
          </Card.Body>
        </Card>

        {/* User's Posts */}
        <div className="mt-5">
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h3 className="h4 fw-bold text-dark position-relative">
              {isOwnProfile ? 'Your Posts' : `${profileUser.username}'s Posts`}
              <span className="position-absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full scale-x-0 hover:scale-x-100 transition-transform duration-300"></span>
            </h3>
            <Badge bg="primary" text="white" pill className="px-3 py-2 shadow-lg">
              {posts.length}
            </Badge>
          </div>

          {posts.length === 0 ? (
            <Card className="text-center p-5 border-0 glass hover:shadow-2xl transition-all duration-300">
              <Card.Body>
                <div className="text-center">
                  <div className="w-20 h-20 mx-auto mb-4 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl d-flex align-items-center justify-center group-hover:scale-110 transition-transform">
                    <Calendar className="w-10 h-10 text-gray-400 group-hover:text-gray-600 transition-colors" />
                  </div>
                  <h3 className="h4 text-dark mb-2 fw-bold">No posts yet</h3>
                  <p className="text-muted mb-4">
                    {isOwnProfile 
                      ? "Your posts will appear here when you create them."
                      : `${profileUser.username} hasn't posted anything yet.`
                    }
                  </p>
                  <Badge bg="info" text="white" pill className="px-3 py-2">
                    <Sparkles className="w-4 h-4 me-2" />
                    {isOwnProfile ? 'Create your first post!' : 'Check back later!'}
                  </Badge>
                </div>
              </Card.Body>
            </Card>
          ) : (
            <div className="space-y-4">
              {posts.map((post, index) => (
                <div key={post._id} className="fade-in hover:scale-[1.01] transition-transform duration-300" style={{ animationDelay: `${index * 0.1}s` }}>
                  <PostCard post={post} />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
