import React, { useState } from 'react';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import { Image, Send, X, Sparkles } from 'lucide-react';

const CreatePost = () => {
  const [content, setContent] = useState('');
  const [image, setImage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showImageInput, setShowImageInput] = useState(false);

  const { createPost, error, clearError } = usePost();
  const { user } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!content.trim()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const result = await createPost({
        content: content.trim(),
        image: image.trim() || null
      });

      if (result.success) {
        setContent('');
        setImage('');
        setShowImageInput(false);
      }
    } catch (error) {
      console.error('Error creating post:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleImageToggle = () => {
    setShowImageInput(!showImageInput);
    if (!showImageInput) {
      setImage('');
    }
  };

  const handleImageRemove = () => {
    setImage('');
    setShowImageInput(false);
  };

  const charCount = content.length;
  const maxChars = 2000;
  const isNearLimit = charCount > maxChars * 0.9;
  const isAtLimit = charCount >= maxChars;

  return (
    <div className="glass rounded-2xl shadow-xl p-6 mb-6 fade-in">
      <form onSubmit={handleSubmit}>
        <div className="flex space-x-4">
          <div className="relative">
            <img
              src={user?.profileImage || 'https://via.placeholder.com/48/667eea/ffffff?text=U'}
              alt={user?.username}
              className="w-12 h-12 rounded-full object-cover border-2 border-white/50"
            />
            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full border-2 border-white"></div>
          </div>

          <div className="flex-1">
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="Share your thoughts with the world..."
              className="w-full resize-none border-0 focus:ring-0 text-gray-900 placeholder-gray-500 text-lg bg-transparent"
              rows={3}
              maxLength={maxChars}
              disabled={isSubmitting}
            />

            {/* Image Preview */}
            {image && (
              <div className="mt-4 relative group">
                <img
                  src={image}
                  alt="Post image"
                  className="w-full max-w-md rounded-xl object-cover shadow-lg transition-all duration-300 group-hover:scale-[1.02]"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    setImage('');
                  }}
                />
                <button
                  type="button"
                  onClick={handleImageRemove}
                  className="absolute top-2 right-2 bg-red-500/80 text-white rounded-full p-2 hover:bg-red-600 transition-all duration-200 backdrop-blur-sm"
                >
                  <X size={16} />
                </button>
                <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-xl"></div>
              </div>
            )}

            {/* Image Input */}
            {showImageInput && !image && (
              <div className="mt-4 slide-up">
                <input
                  type="url"
                  value={image}
                  onChange={(e) => setImage(e.target.value)}
                  placeholder="Enter image URL (optional)"
                  className="w-full px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent bg-white/80 backdrop-blur-sm"
                  disabled={isSubmitting}
                />
              </div>
            )}
          </div>
        </div>

        {/* Actions Bar */}
        <div className="flex items-center justify-between mt-6 pt-4 border-t border-white/10">
          <div className="flex items-center space-x-3">
            <button
              type="button"
              onClick={handleImageToggle}
              className={`flex items-center space-x-2 px-4 py-2 rounded-xl text-sm font-medium transition-all duration-300 ${
                showImageInput || image
                  ? 'text-purple-600 bg-purple-100' 
                  : 'text-gray-600 hover:text-purple-600 hover:bg-purple-100'
              }`}
              disabled={isSubmitting}
            >
              <div className={`p-1.5 rounded-lg transition-colors ${
                showImageInput || image ? 'bg-purple-200' : 'hover:bg-purple-200'
              }`}>
                <Image size={18} />
              </div>
              <span>{image ? 'Image Added' : 'Add Image'}</span>
            </button>
          </div>

          <div className="flex items-center space-x-4">
            {/* Character Count */}
            <div className="flex items-center">
              <span className={`text-xs font-medium transition-colors ${
                isAtLimit ? 'text-red-500' : isNearLimit ? 'text-yellow-500' : 'text-gray-500'
              }`}>
                {charCount}/{maxChars}
              </span>
              {isNearLimit && (
                <div className="ml-2 w-2 h-2 rounded-full animate-pulse ${
                  isAtLimit ? 'bg-red-500' : 'bg-yellow-500'
                }"></div>
              )}
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              disabled={!content.trim() || isSubmitting || isAtLimit}
              className="flex items-center space-x-2 px-6 py-2.5 bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white text-sm font-medium rounded-xl focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300 btn-hover glow"
            >
              {isSubmitting ? (
                <>
                  <svg className="animate-spin h-4 w-4" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  <span>Posting...</span>
                </>
              ) : (
                <>
                  <Send size={16} />
                  <span>Post</span>
                </>
              )}
            </button>
          </div>
        </div>

        {/* Error Message */}
        {error && (
          <div className="mt-4 glass-dark border border-red-200/50 rounded-xl p-4 slide-up">
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <span className="text-sm text-red-300">{error}</span>
              </div>
              <button
                type="button"
                onClick={clearError}
                className="text-red-400 hover:text-red-300 transition-colors"
              >
                <X size={16} />
              </button>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default CreatePost;
