import React, { createContext, useContext, useReducer, useEffect } from 'react';
import axios from 'axios';

// Set base URL for axios
axios.defaults.baseURL = 'http://localhost:5000';

// Create context
const PostContext = createContext();

// Initial state
const initialState = {
  posts: [],
  currentPost: null,
  loading: false,
  error: null,
  pagination: {
    page: 1,
    limit: 10,
    total: 0,
    pages: 0
  }
};

// Action types
const POST_LOADING = 'POST_LOADING';
const POST_SUCCESS = 'POST_SUCCESS';
const POST_FAIL = 'POST_FAIL';
const POST_CREATE = 'POST_CREATE';
const POST_UPDATE = 'POST_UPDATE';
const POST_DELETE = 'POST_DELETE';
const POST_LIKE = 'POST_LIKE';
const POST_UNLIKE = 'POST_UNLIKE';
const SET_CURRENT_POST = 'SET_CURRENT_POST';
const CLEAR_ERROR = 'CLEAR_ERROR';
const CLEAR_CURRENT_POST = 'CLEAR_CURRENT_POST';

// Reducer
const postReducer = (state, action) => {
  switch (action.type) {
    case POST_LOADING:
      return {
        ...state,
        loading: true,
        error: null
      };
    case POST_SUCCESS:
      return {
        ...state,
        posts: action.payload.posts,
        pagination: action.payload.pagination,
        loading: false,
        error: null
      };
    case POST_FAIL:
      return {
        ...state,
        loading: false,
        error: action.payload
      };
    case POST_CREATE:
      return {
        ...state,
        posts: [action.payload, ...state.posts],
        loading: false,
        error: null
      };
    case POST_UPDATE:
      return {
        ...state,
        posts: state.posts.map(post =>
          post._id === action.payload._id ? action.payload : post
        ),
        currentPost: state.currentPost?._id === action.payload._id 
          ? action.payload 
          : state.currentPost,
        loading: false,
        error: null
      };
    case POST_DELETE:
      return {
        ...state,
        posts: state.posts.filter(post => post._id !== action.payload),
        currentPost: state.currentPost?._id === action.payload ? null : state.currentPost,
        loading: false,
        error: null
      };
    case POST_LIKE:
      return {
        ...state,
        posts: state.posts.map(post =>
          post._id === action.payload.postId
            ? { ...post, likes: [...post.likes, action.payload.userId], likesCount: post.likesCount + 1 }
            : post
        ),
        currentPost: state.currentPost?._id === action.payload.postId
          ? { ...state.currentPost, likes: [...state.currentPost.likes, action.payload.userId], likesCount: state.currentPost.likesCount + 1 }
          : state.currentPost,
        loading: false
      };
    case POST_UNLIKE:
      return {
        ...state,
        posts: state.posts.map(post =>
          post._id === action.payload.postId
            ? { ...post, likes: post.likes.filter(id => id !== action.payload.userId), likesCount: post.likesCount - 1 }
            : post
        ),
        currentPost: state.currentPost?._id === action.payload.postId
          ? { ...state.currentPost, likes: state.currentPost.likes.filter(id => id !== action.payload.userId), likesCount: state.currentPost.likesCount - 1 }
          : state.currentPost,
        loading: false
      };
    case SET_CURRENT_POST:
      return {
        ...state,
        currentPost: action.payload,
        loading: false,
        error: null
      };
    case CLEAR_ERROR:
      return {
        ...state,
        error: null
      };
    case CLEAR_CURRENT_POST:
      return {
        ...state,
        currentPost: null
      };
    default:
      return state;
  }
};

// Post provider component
export const PostProvider = ({ children }) => {
  const [state, dispatch] = useReducer(postReducer, initialState);

  // Get all posts (feed)
  const getPosts = async (page = 1, limit = 10) => {
    try {
      dispatch({ type: POST_LOADING });

      const res = await axios.get(`/api/posts?page=${page}&limit=${limit}`);

      dispatch({
        type: POST_SUCCESS,
        payload: res.data.data
      });

      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to fetch posts';
      dispatch({
        type: POST_FAIL,
        payload: message
      });
      return { success: false, message };
    }
  };

  // Get single post
  const getPost = async (postId) => {
    try {
      dispatch({ type: POST_LOADING });

      const res = await axios.get(`/api/posts/${postId}`);

      dispatch({
        type: SET_CURRENT_POST,
        payload: res.data.data.post
      });

      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to fetch post';
      dispatch({
        type: POST_FAIL,
        payload: message
      });
      return { success: false, message };
    }
  };

  // Create post
  const createPost = async (formData) => {
    try {
      dispatch({ type: POST_LOADING });

      const res = await axios.post('/api/posts', formData);

      dispatch({
        type: POST_CREATE,
        payload: res.data.data.post
      });

      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to create post';
      dispatch({
        type: POST_FAIL,
        payload: message
      });
      return { success: false, message };
    }
  };

  // Update post
  const updatePost = async (postId, formData) => {
    try {
      dispatch({ type: POST_LOADING });

      const res = await axios.put(`/api/posts/${postId}`, formData);

      dispatch({
        type: POST_UPDATE,
        payload: res.data.data.post
      });

      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to update post';
      dispatch({
        type: POST_FAIL,
        payload: message
      });
      return { success: false, message };
    }
  };

  // Delete post
  const deletePost = async (postId) => {
    try {
      dispatch({ type: POST_LOADING });

      await axios.delete(`/api/posts/${postId}`);

      dispatch({
        type: POST_DELETE,
        payload: postId
      });

      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to delete post';
      dispatch({
        type: POST_FAIL,
        payload: message
      });
      return { success: false, message };
    }
  };

  // Like/unlike post
  const toggleLike = async (postId) => {
    try {
      const res = await axios.post(`/api/posts/${postId}/like`);

      const { isLiked } = res.data.data;

      dispatch({
        type: isLiked ? POST_LIKE : POST_UNLIKE,
        payload: { postId, userId: JSON.parse(localStorage.getItem('user'))?._id }
      });

      return { success: true, isLiked };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to toggle like';
      dispatch({
        type: POST_FAIL,
        payload: message
      });
      return { success: false, message };
    }
  };

  // Clear error
  const clearError = () => {
    dispatch({ type: CLEAR_ERROR });
  };

  // Clear current post
  const clearCurrentPost = () => {
    dispatch({ type: CLEAR_CURRENT_POST });
  };

  return (
    <PostContext.Provider
      value={{
        ...state,
        getPosts,
        getPost,
        createPost,
        updatePost,
        deletePost,
        toggleLike,
        clearError,
        clearCurrentPost
      }}
    >
      {children}
    </PostContext.Provider>
  );
};

// Custom hook to use post context
export const usePost = () => {
  const context = useContext(PostContext);
  if (!context) {
    throw new Error('usePost must be used within a PostProvider');
  }
  return context;
};

export default PostContext;
