import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Search as SearchIcon, Users, Loader2 } from 'lucide-react';

const Search = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      if (query.trim()) {
        handleSearch();
      } else {
        setResults([]);
        setSearched(false);
      }
    }, 500);

    return () => clearTimeout(timer);
  }, [query]);

  const handleSearch = async () => {
    if (!query.trim()) return;

    setLoading(true);
    setSearched(true);

    try {
      const response = await fetch(`/api/users?search=${encodeURIComponent(query.trim())}`);
      const data = await response.json();

      if (data.success) {
        setResults(data.data.users);
      }
    } catch (error) {
      console.error('Error searching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handleSearch();
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-900 mb-4">Search Users</h1>

        {/* Search Form */}
        <form onSubmit={handleSubmit} className="relative">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <SearchIcon className="h-5 w-5 text-gray-400" />
            </div>
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search by username or bio..."
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent text-lg"
              autoFocus
            />
          </div>
        </form>
      </div>

      {/* Loading State */}
      {loading && (
        <div className="flex items-center justify-center py-8">
          <div className="text-center">
            <Loader2 className="animate-spin h-6 w-6 text-primary-600 mx-auto mb-2" />
            <p className="text-sm text-gray-600">Searching...</p>
          </div>
        </div>
      )}

      {/* Search Results */}
      {!loading && searched && (
        <div>
          {results.length === 0 ? (
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8 text-center">
              <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                {query.trim() ? 'No users found' : 'Start searching'}
              </h3>
              <p className="text-gray-600">
                {query.trim() 
                  ? `No users found matching "${query.trim()}". Try different keywords.`
                  : 'Enter a username or bio keywords to search for users.'
                }
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              <div className="text-sm text-gray-500 mb-2">
                Found {results.length} user{results.length !== 1 ? 's' : ''} 
                {query.trim() && ` matching "${query.trim()}"`}
              </div>

              {results.map((user) => (
                <div
                  key={user._id}
                  className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 hover:shadow-md transition-shadow"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <Link to={`/users/${user._id}`}>
                        <img
                          src={user.profileImage || 'https://via.placeholder.com/48/3b82f6/ffffff?text=U'}
                          alt={user.username}
                          className="w-12 h-12 rounded-full object-cover hover:opacity-90 transition-opacity"
                        />
                      </Link>

                      <div>
                        <Link
                          to={`/users/${user._id}`}
                          className="font-medium text-gray-900 hover:text-primary-600 transition-colors"
                        >
                          {user.username}
                        </Link>
                        <p className="text-sm text-gray-500 line-clamp-2">
                          {user.bio || 'No bio available'}
                        </p>
                        <div className="flex items-center space-x-4 mt-1">
                          <span className="text-xs text-gray-400">
                            {user.followersCount} followers
                          </span>
                          <span className="text-xs text-gray-400">
                            {user.followingCount} following
                          </span>
                        </div>
                      </div>
                    </div>

                    <Link
                      to={`/users/${user._id}`}
                      className="px-4 py-2 text-sm font-medium text-primary-600 hover:text-primary-700 hover:bg-primary-50 rounded-md transition-colors"
                    >
                      View Profile
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Search Tips */}
      {!searched && !loading && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
          <h3 className="text-lg font-medium text-blue-900 mb-3">Search Tips</h3>
          <ul className="space-y-2 text-sm text-blue-800">
            <li className="flex items-start">
              <span className="mr-2">•</span>
              <span>Search by username to find specific users</span>
            </li>
            <li className="flex items-start">
              <span className="mr-2">•</span>
              <span>Use keywords to search user bios</span>
            </li>
            <li className="flex items-start">
              <span className="mr-2">•</span>
              <span>Search is case-insensitive</span>
            </li>
            <li className="flex items-start">
              <span className="mr-2">•</span>
              <span>Results update automatically as you type</span>
            </li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default Search;
