import React, { useState, useEffect } from 'react';
import { usePost } from '../context/PostContext';
import { useAuth } from '../context/AuthContext';
import PostCard from '../components/PostCard';
import CreatePost from '../components/CreatePost';
import { Loader2, RefreshCw, Home as HomeIcon, Sparkles } from 'lucide-react';
import { Card, Button, Badge, Spinner, Alert } from 'react-bootstrap';

const Home = () => {
  const { posts, loading, error, getPosts } = usePost();
  const { user } = useAuth();
  const [page, setPage] = useState(1);
  const [refreshing, setRefreshing] = useState(false);

  useEffect(() => {
    getPosts(page);
  }, [page]);

  const handleRefresh = async () => {
    setRefreshing(true);
    await getPosts(1);
    setPage(1);
    setRefreshing(false);
  };

  const handleLoadMore = () => {
    setPage(prev => prev + 1);
  };

  return (
    <div className="min-h-screen">
      <div className="container-fluid px-4 py-4">
        {/* Header Section */}
        <Card className="glass rounded-3 shadow-xl mb-4 border-0 fade-in">
          <Card.Body className="p-4">
            <div className="d-flex justify-content-between align-items-center">
              <div className="d-flex align-items-center">
                <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-pink-500 rounded-xl d-flex align-items-center justify-center glow me-3 floating">
                  <HomeIcon className="w-6 h-6 text-white" />
                </div>
                <h1 className="h2 mb-0 fw-bold text-dark position-relative">
                  Home Feed
                  <span className="position-absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full scale-x-0 hover:scale-x-100 transition-transform duration-300"></span>
                </h1>
              </div>
              <Button
                onClick={handleRefresh}
                disabled={refreshing}
                className="btn-hover px-4 py-2 rounded-xl shadow-md hover:shadow-lg transition-all duration-300"
                variant="outline-primary"
              >
                <RefreshCw className={`w-4 h-4 me-2 ${refreshing ? 'animate-spin' : ''}`} />
                {refreshing ? 'Refreshing...' : 'Refresh'}
              </Button>
            </div>
          </Card.Body>
        </Card>

        {/* Create Post Section */}
        <CreatePost />

        {/* Error Message */}
        {error && (
          <Alert variant="danger" className="mb-4 fade-in">
            <div className="d-flex align-items-center">
              <Sparkles className="w-5 h-5 text-danger me-2" />
              <span className="text-danger">{error}</span>
            </div>
          </Alert>
        )}

        {/* Loading State */}
        {loading && posts.length === 0 && (
          <Card className="text-center p-5 border-0 glass mb-4">
            <Card.Body>
              <div className="text-center">
                <div className="w-16 h-16 mx-auto mb-4 bg-gradient-to-br from-purple-100 to-pink-100 rounded-2xl d-flex align-items-center justify-center">
                  <Loader2 className="w-8 h-8 text-purple-600 animate-spin" />
                </div>
                <h3 className="h5 text-dark mb-2 fw-bold">Loading Posts</h3>
                <p className="text-muted">Please wait while we fetch the latest posts...</p>
              </div>
            </Card.Body>
          </Card>
        )}

        {/* Empty State */}
        {!loading && posts.length === 0 && !error && (
          <Card className="text-center p-5 border-0 glass mb-4 hover:shadow-2xl transition-all duration-300">
            <Card.Body>
              <div className="text-center">
                <div className="w-20 h-20 mx-auto mb-4 bg-gradient-to-br from-gray-100 to-gray-200 rounded-2xl d-flex align-items-center justify-center group-hover:scale-110 transition-transform">
                  <HomeIcon className="w-10 h-10 text-gray-400 group-hover:text-gray-600 transition-colors" />
                </div>
                <h3 className="h4 text-dark mb-2 fw-bold">No posts yet</h3>
                <p className="text-muted mb-4">
                  {user?.following?.length > 0 
                    ? "Posts from people you follow will appear here"
                    : "Start following people to see their posts here"
                  }
                </p>
                <Badge bg="primary" text="white" pill className="px-3 py-2">
                  <Sparkles className="w-4 h-4 me-2" />
                  Create your first post to get started!
                </Badge>
              </div>
            </Card.Body>
          </Card>
        )}

        {/* Posts Feed */}
        {posts.length > 0 && (
          <div className="space-y-4">
            {posts.map((post, index) => (
              <div key={post._id} className="fade-in hover:scale-[1.01] transition-transform duration-300" style={{ animationDelay: `${index * 0.1}s` }}>
                <PostCard post={post} />
              </div>
            ))}
          </div>
        )}

        {/* Load More Button */}
        {posts.length > 0 && !loading && (
          <div className="text-center mt-5">
            <Button
              onClick={handleLoadMore}
              className="btn-hover glow px-6 py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-300"
              variant="primary"
            >
              <Sparkles className="w-4 h-4 me-2" />
              Load More Posts
            </Button>
          </div>
        )}

        {/* Loading More State */}
        {loading && posts.length > 0 && (
          <Card className="text-center p-4 border-0 glass mt-4">
            <Card.Body>
              <div className="d-flex align-items-center justify-content-center">
                <Spinner as="span" animation="border" className="me-3" />
                <span className="text-muted">Loading more posts...</span>
              </div>
            </Card.Body>
          </Card>
        )}
      </div>
    </div>
  );
};

export default Home;
